import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-1202.1 Vehicle Removal Certificates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1202.1.md","filePath":"vacode/46.2-1202.1.md"}'),o={name:"vacode/46.2-1202.1.md"},i=r('<h1 id="§-46-2-1202-1-vehicle-removal-certificates" tabindex="-1">§ 46.2-1202.1 Vehicle Removal Certificates. <a class="header-anchor" href="#§-46-2-1202-1-vehicle-removal-certificates" aria-label="Permalink to &quot;§ 46.2-1202.1 Vehicle Removal Certificates.&quot;">​</a></h1><p>The person in possession of an abandoned vehicle shall obtain from the Department in a manner prescribed by the Commissioner, a Vehicle Removal Certificate at no fee. If the Department finds no record for the vehicle, the vehicle may then be sold or transferred to a licensee or a scrap metal processor, as defined in § <a href="/vacode/46.2-1600/">46.2-1600</a>. Upon such sale or transfer, the completed Vehicle Removal Certificate and receipt produced pursuant to § <a href="/vacode/46.2-1202/">46.2-1202</a> shall be given to the licensee or scrap metal processor.</p><p>2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0664" target="_blank" rel="noreferrer">664</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0374" target="_blank" rel="noreferrer">374</a>.</p>',3),c=[i];function s(n,l,h,d,p,f){return a(),t("div",null,c)}const v=e(o,[["render",s]]);export{m as __pageData,v as default};
