import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1225 Enforcement provisions in city or county parking ordinances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1225.md","filePath":"vacode/46.2-1225.md"}'),t={name:"vacode/46.2-1225.md"},o=n('<h1 id="§-46-2-1225-enforcement-provisions-in-city-or-county-parking-ordinances" tabindex="-1">§ 46.2-1225 Enforcement provisions in city or county parking ordinances. <a class="header-anchor" href="#§-46-2-1225-enforcement-provisions-in-city-or-county-parking-ordinances" aria-label="Permalink to &quot;§ 46.2-1225 Enforcement provisions in city or county parking ordinances.&quot;">​</a></h1><p>Any city or county ordinance regulating parking under this article shall require:</p><ol><li><p>That uncontested payment of parking citation penalties be collected and accounted for by a local administrative official or officials who shall be compensated by the locality or by a private management company under contract with the locality;</p></li><li><p>That contest by any person of any parking citation shall be certified on an appropriate form, to the appropriate district court, by such official or officials; and</p></li><li><p>That the local administrative official or officials shall cause complaints, summons, or warrants to be issued for delinquent parking citations.</p></li></ol><p>Every action to collect unpaid parking citation penalties imposed for violation of a city or county ordinance regulating parking under this article shall be commenced within three years of the date upon which such penalty became delinquent.</p><p>1974, c. 403, § 46.1-254.1; 1975, c. 440; 1977, c. 671; 1980, c. 355; 1989, c. 727; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0459" target="_blank" rel="noreferrer">459</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0563" target="_blank" rel="noreferrer">563</a>.</p>',5),r=[o];function c(s,l,p,d,f,h){return i(),a("div",null,r)}const m=e(t,[["render",c]]);export{_ as __pageData,m as default};
