import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-1227 Enforcement of state regulations governing parking on primary and secondary highways.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1227.md","filePath":"vacode/46.2-1227.md"}'),r={name:"vacode/46.2-1227.md"},i=o('<h1 id="§-46-2-1227-enforcement-of-state-regulations-governing-parking-on-primary-and-secondary-highways" tabindex="-1">§ 46.2-1227 Enforcement of state regulations governing parking on primary and secondary highways. <a class="header-anchor" href="#§-46-2-1227-enforcement-of-state-regulations-governing-parking-on-primary-and-secondary-highways" aria-label="Permalink to &quot;§ 46.2-1227 Enforcement of state regulations governing parking on primary and secondary highways.&quot;">​</a></h1><p>Any regulation of the Commissioner under the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-1223/" target="_blank" rel="noreferrer">46.2-1223</a> relating to parking on any primary or secondary highway shall provide:</p><ol><li><p>That uncontested citations issued under the regulation shall be paid to the administrative official or officials appointed under the provisions of this section in the locality in which the part of the highway lies, or in the locality where there is no appointed administrative official the citations shall be paid to the local treasurer, who shall promptly pay them into the general fund of the state treasury; and</p></li><li><p>That contested or delinquent citations shall be certified or complaint, summons, or warrant shall be issued as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-1225/" target="_blank" rel="noreferrer">46.2-1225</a> to the general district court in whose jurisdiction the part of the highway lies. Any sums collected by such court, minus court costs, shall be promptly paid by the clerk into the general fund of the state treasury.</p></li></ol><p>1974, c. 403, § 46.1-254.1; 1975, c. 440; 1977, c. 671; 1980, c. 355; 1989, c. 727.</p>',4),n=[i];function s(l,h,c,d,p,g){return t(),a("div",null,n)}const m=e(r,[["render",s]]);export{u as __pageData,m as default};
