import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-1229 Enforcement of parking regulations of State Board of Behavioral Health and Developmental Services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1229.md","filePath":"vacode/46.2-1229.md"}'),r={name:"vacode/46.2-1229.md"},i=o('<h1 id="§-46-2-1229-enforcement-of-parking-regulations-of-state-board-of-behavioral-health-and-developmental-services" tabindex="-1">§ 46.2-1229 Enforcement of parking regulations of State Board of Behavioral Health and Developmental Services. <a class="header-anchor" href="#§-46-2-1229-enforcement-of-parking-regulations-of-state-board-of-behavioral-health-and-developmental-services" aria-label="Permalink to &quot;§ 46.2-1229 Enforcement of parking regulations of State Board of Behavioral Health and Developmental Services.&quot;">​</a></h1><p>Any regulations of the State Board of Behavioral Health and Developmental Services pursuant to the provisions of § <a href="http://law.lis.virginia.gov/vacode/37.2-203/" target="_blank" rel="noreferrer">37.2-203</a> relating to parking on property owned or controlled by the Department of Behavioral Health and Developmental Services shall provide:</p><ol><li><p>That uncontested citations issued thereunder shall be paid to the administrative official or officials appointed under the provisions of this section in the locality in which the part of the state facility lies, who shall promptly deposit the sums into the state treasury as a special revenue of the Department of Behavioral Health and Developmental Services; and</p></li><li><p>That contested or delinquent citations shall be certified or complaint, summons, or warrant shall be issued as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-1225/" target="_blank" rel="noreferrer">46.2-1225</a> to the general district court in whose jurisdiction the state facility lies. Any sum collected by the court, minus court costs, shall be promptly deposited by the clerk into the state treasury as a special revenue of the Department of Behavioral Health and Developmental Services.</p></li></ol><p>1974, c. 403, § 46.1-254.1; 1975, c. 440; 1977, c. 671; 1980, c. 355; 1989, c. 727; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>.</p>',4),n=[i];function l(s,c,h,p,d,f){return a(),t("div",null,n)}const u=e(r,[["render",l]]);export{m as __pageData,u as default};
