import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-1233.3 Improper towing; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1233.3.md","filePath":"vacode/46.2-1233.3.md"}'),o={name:"vacode/46.2-1233.3.md"},n=t('<h1 id="§-46-2-1233-3-improper-towing-penalty" tabindex="-1">§ 46.2-1233.3 Improper towing; penalty. <a class="header-anchor" href="#§-46-2-1233-3-improper-towing-penalty" aria-label="Permalink to &quot;§ 46.2-1233.3 Improper towing; penalty.&quot;">​</a></h1><p>A. This section shall apply only to tow truck drivers and towing and recovery operators removing a vehicle without the consent of its owner from a location in Planning District 8.</p><p>B. In addition to any action brought pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-119/" target="_blank" rel="noreferrer">46.2-119</a>, any tow truck driver who violates subsection A of § <a href="http://law.lis.virginia.gov/vacode/46.2-118/" target="_blank" rel="noreferrer">46.2-118</a> or § <a href="http://law.lis.virginia.gov/vacode/46.2-1217/" target="_blank" rel="noreferrer">46.2-1217</a>, <a href="http://law.lis.virginia.gov/vacode/46.2-1231/" target="_blank" rel="noreferrer">46.2-1231</a>, or <a href="http://law.lis.virginia.gov/vacode/46.2-1233.1/" target="_blank" rel="noreferrer">46.2-1233.1</a>, or any ordinance adopted therefrom, or any ordinance adopted pursuant to § <a href="http://law.lis.virginia.gov/vacode/46.2-1233/" target="_blank" rel="noreferrer">46.2-1233</a>, or any towing or recovery operator who violates subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-118/" target="_blank" rel="noreferrer">46.2-118</a> or § <a href="http://law.lis.virginia.gov/vacode/46.2-1217/" target="_blank" rel="noreferrer">46.2-1217</a>, <a href="http://law.lis.virginia.gov/vacode/46.2-1231/" target="_blank" rel="noreferrer">46.2-1231</a>, or <a href="http://law.lis.virginia.gov/vacode/46.2-1233.1/" target="_blank" rel="noreferrer">46.2-1233.1</a>, or any ordinance adopted therefrom, or any ordinance adopted pursuant to § <a href="http://law.lis.virginia.gov/vacode/46.2-1233/" target="_blank" rel="noreferrer">46.2-1233</a>, is subject to a civil penalty of $150 per violation. Such penalty shall be collected by the Office of the Attorney General, and the proceeds shall be deposited into the Literary Fund.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0825" target="_blank" rel="noreferrer">825</a>.</p>',4),i=[n];function l(c,p,s,d,h,v){return r(),a("div",null,i)}const _=e(o,[["render",l]]);export{f as __pageData,_ as default};
