import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-1233 Localities may regulate towing fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1233.md","filePath":"vacode/46.2-1233.md"}'),t={name:"vacode/46.2-1233.md"},o=r('<h1 id="§-46-2-1233-localities-may-regulate-towing-fees" tabindex="-1">§ 46.2-1233 Localities may regulate towing fees. <a class="header-anchor" href="#§-46-2-1233-localities-may-regulate-towing-fees" aria-label="Permalink to &quot;§ 46.2-1233 Localities may regulate towing fees.&quot;">​</a></h1><p>The governing body of any locality may by ordinance set reasonable limits on fees charged for the removal of motor vehicles, trailers, and parts thereof left on private property in violation of § <a href="/vacode/46.2-1231/">46.2-1231</a>, and for the removal of trespassing vehicles under § <a href="/vacode/46.2-1215/">46.2-1215</a>, taking into consideration the fair market value of such removal.</p><p>Localities in Planning District 8 and Planning District 16 shall establish by ordinance (i) a hookup and initial towing fee of no less than $135 and no more than the maximum charges provided in § <a href="/vacode/46.2-1233.1/">46.2-1233.1</a> and (ii) for towing a vehicle between 7:00 p.m. and 8:00 a.m. or on any Saturday, Sunday, or holiday, an additional fee of no less than $25 and no more than the maximum charges provided in § <a href="/vacode/46.2-1233.1/">46.2-1233.1</a> per instance; however, such ordinance shall also provide that in no event shall more than two such additional fees be charged for towing any vehicle.</p><p>Code 1950, § 46-541; 1952, c. 352; 1954, c. 435; 1958, c. 541, § 46.1-551; 1978, cc. 202, 335; 1979, c. 132; 1983, c. 34; 1985, c. 375; 1989, cc. 17, 727; 1990, cc. 502, 571, 573; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0411" target="_blank" rel="noreferrer">411</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0412" target="_blank" rel="noreferrer">412</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0117" target="_blank" rel="noreferrer">117</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0460" target="_blank" rel="noreferrer">460</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0031" target="_blank" rel="noreferrer">31</a>.</p>',4),n=[o];function l(c,s,h,f,g,d){return a(),i("div",null,n)}const _=e(t,[["render",l]]);export{v as __pageData,_ as default};
