import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1304 Local regulation of trucks and buses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1304.md","filePath":"vacode/46.2-1304.md"}'),i={name:"vacode/46.2-1304.md"},a=o('<h1 id="§-46-2-1304-local-regulation-of-trucks-and-buses" tabindex="-1">§ 46.2-1304 Local regulation of trucks and buses. <a class="header-anchor" href="#§-46-2-1304-local-regulation-of-trucks-and-buses" aria-label="Permalink to &quot;§ 46.2-1304 Local regulation of trucks and buses.&quot;">​</a></h1><p>The governing bodies of counties, cities, and towns may by ordinance, whenever in their judgment conditions so require:</p><ol><li><p>Prohibit the use of trucks, except for the purpose of receiving loads or making deliveries on certain designated streets under their jurisdiction;</p></li><li><p>Restrict the use of trucks passing through the city or town to such street or streets under their jurisdiction as may be designated in such ordinance.</p></li></ol><p>The Cities of Poquoson and Williamsburg may restrict the operation of nonscheduled buses, other than school buses, over designated streets under its jurisdiction.</p><p>Code 1950, § 46-206; 1958, c. 541, § 46.1-181; 1968, c. 463; 1989, c. 727; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0547" target="_blank" rel="noreferrer">547</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0574" target="_blank" rel="noreferrer">574</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>.</p>',5),s=[a];function n(c,l,d,u,h,g){return t(),r("div",null,s)}const f=e(i,[["render",n]]);export{_ as __pageData,f as default};
