import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1307 Designation of private roads as highways for law-enforcement purposes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1307.md","filePath":"vacode/46.2-1307.md"}'),i={name:"vacode/46.2-1307.md"},t=r('<h1 id="§-46-2-1307-designation-of-private-roads-as-highways-for-law-enforcement-purposes" tabindex="-1">§ 46.2-1307 Designation of private roads as highways for law-enforcement purposes. <a class="header-anchor" href="#§-46-2-1307-designation-of-private-roads-as-highways-for-law-enforcement-purposes" aria-label="Permalink to &quot;§ 46.2-1307 Designation of private roads as highways for law-enforcement purposes.&quot;">​</a></h1><p>The governing body of any county, city, or town may adopt ordinances designating the private roads, within any residential development containing 100 or more lots or residential dwelling units, as highways for law-enforcement purposes. Such ordinance may also provide for certification of road signs and speed limits by private licensed professional engineers using criteria developed by the Commissioner of Highways, and, for law-enforcement purposes, such certification shall have the same effect as if certified by the Commissioner of Highways.</p><p>1979, c. 100, § 46.1-181.5; 1987, c. 152; 1989, c. 727; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0074" target="_blank" rel="noreferrer">74</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0187" target="_blank" rel="noreferrer">187</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0310" target="_blank" rel="noreferrer">310</a>.</p>',3),n=[t];function s(c,f,l,p,d,h){return a(),o("div",null,n)}const m=e(i,[["render",s]]);export{_ as __pageData,m as default};
