import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-1503.2 State Personnel and Public Procurement Acts not applicable.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1503.2.md","filePath":"vacode/46.2-1503.2.md"}'),n={name:"vacode/46.2-1503.2.md"},o=r('<h1 id="§-46-2-1503-2-state-personnel-and-public-procurement-acts-not-applicable" tabindex="-1">§ 46.2-1503.2 State Personnel and Public Procurement Acts not applicable. <a class="header-anchor" href="#§-46-2-1503-2-state-personnel-and-public-procurement-acts-not-applicable" aria-label="Permalink to &quot;§ 46.2-1503.2 State Personnel and Public Procurement Acts not applicable.&quot;">​</a></h1><p>A. The Executive Director and all staff employed by the Board shall be exempt from the Virginia Personnel Act (§ <a href="/vacode/2.2-2900/">2.2-2900</a> et seq.) of Title 2.2. Personnel actions under this exemption shall be taken without regard to race, sex, sexual orientation, gender identity, color, national origin, religion, age, handicap, or political affiliation.</p><p>B. The Board and the Executive Director shall be exempt from the Virginia Public Procurement Act (§ <a href="/vacode/2.2-4300/">2.2-4300</a> et seq.) of Title 2.2.</p><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1137" target="_blank" rel="noreferrer">1137</a>.</p>',4),i=[o];function l(c,s,p,d,_,h){return t(),a("div",null,i)}const b=e(n,[["render",l]]);export{u as __pageData,b as default};
