import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-1506 Regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1506.md","filePath":"vacode/46.2-1506.md"}'),o={name:"vacode/46.2-1506.md"},i=r('<h1 id="§-46-2-1506-regulations" tabindex="-1">§ 46.2-1506 Regulations. <a class="header-anchor" href="#§-46-2-1506-regulations" aria-label="Permalink to &quot;§ 46.2-1506 Regulations.&quot;">​</a></h1><p>The Board may promulgate regulations requiring persons licensed under this chapter to keep and maintain records reasonably required for the enforcement of §§ <a href="http://law.lis.virginia.gov/vacode/46.2-112/" target="_blank" rel="noreferrer">46.2-112</a> and <a href="http://law.lis.virginia.gov/vacode/46.2-629/" target="_blank" rel="noreferrer">46.2-629</a>, and any other regulations, not inconsistent with the provisions of this chapter, as it shall consider necessary for the effective administration and enforcement of this chapter. A copy of any regulation promulgated under this section shall be mailed to each motor vehicle dealer licensee thirty days prior to its effective date.</p><p>Code 1950, § 46-507; 1958, c. 541, § 46.1-520; 1986, c. 490; 1988, c. 865; 1989, c. 727; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>.</p>',3),n=[i];function s(c,l,d,h,_,f){return a(),t("div",null,n)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
