import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-1508.1 Licensure of certain nonprofit organizations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1508.1.md","filePath":"vacode/46.2-1508.1.md"}'),a={name:"vacode/46.2-1508.1.md"},r=i('<h1 id="§-46-2-1508-1-licensure-of-certain-nonprofit-organizations" tabindex="-1">§ 46.2-1508.1 Licensure of certain nonprofit organizations. <a class="header-anchor" href="#§-46-2-1508-1-licensure-of-certain-nonprofit-organizations" aria-label="Permalink to &quot;§ 46.2-1508.1 Licensure of certain nonprofit organizations.&quot;">​</a></h1><p>A. Any nonprofit organization exempt from taxation under § 501(c) (3) of the Internal Revenue Code that (i) receives title to motor vehicles as qualified charitable gifts to the organization, (ii) provides no more than twelve of these donated vehicles in any twelve-month period to low-income persons, as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-5400/" target="_blank" rel="noreferrer">2.2-5400</a>, in need of transportation, and (iii) receives from the recipients of the vehicles only reimbursement for the costs of repairs, towing, titles, taxes, license fees and inspection fees shall be required to obtain a dealer&#39;s license. However, such nonprofit organization shall be exempt from the requirements of § <a href="http://law.lis.virginia.gov/vacode/46.2-1510/" target="_blank" rel="noreferrer">46.2-1510</a>, Article 3.1 (§ <a href="http://law.lis.virginia.gov/vacode/46.2-1527.1/" target="_blank" rel="noreferrer">46.2-1527.1</a> et seq.) of Chapter 15 of this title, §§ <a href="http://law.lis.virginia.gov/vacode/46.2-1533/" target="_blank" rel="noreferrer">46.2-1533</a>, and <a href="http://law.lis.virginia.gov/vacode/46.2-1534/" target="_blank" rel="noreferrer">46.2-1534</a>. Transactions of such nonprofit organization shall not be subject to recovery from the Motor Vehicle Transaction Recovery Fund.</p><p>B. Upon application to and approval by the Board, any nonprofit organization exempt from taxation under § 501(c) (3) of the Internal Revenue Code may be issued a nonprofit organization certificate authorizing it to consign donated motor vehicles to licensed Virginia motor vehicle dealers when the nonprofit organization receives title to such motor vehicles as qualified charitable gifts and titles the vehicles in the name of the nonprofit organization.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0393" target="_blank" rel="noreferrer">393</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0180" target="_blank" rel="noreferrer">180</a>.</p>',4),n=[r];function s(l,c,f,h,p,d){return t(),o("div",null,n)}const _=e(a,[["render",s]]);export{v as __pageData,_ as default};
