import{_ as e,o as a,c as i,O as s}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-1510 Dealers required to have established place of business.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1510.md","filePath":"vacode/46.2-1510.md"}'),r={name:"vacode/46.2-1510.md"},t=s('<h1 id="§-46-2-1510-dealers-required-to-have-established-place-of-business" tabindex="-1">§ 46.2-1510 Dealers required to have established place of business. <a class="header-anchor" href="#§-46-2-1510-dealers-required-to-have-established-place-of-business" aria-label="Permalink to &quot;§ 46.2-1510 Dealers required to have established place of business.&quot;">​</a></h1><p>No license shall be issued to any motor vehicle dealer unless he has an established place of business, owned or leased by him, where a substantial portion of the sales activity of the business is routinely conducted and which:</p><ol><li><p>Satisfies all local zoning regulations;</p></li><li><p>Has sales, service, and office space devoted exclusively to the dealership of at least 250 square feet in a permanent, enclosed building not used as a residence;</p></li><li><p>Houses all records the dealer is required to maintain by § <a href="http://law.lis.virginia.gov/vacode/46.2-1529/" target="_blank" rel="noreferrer">46.2-1529</a>;</p></li><li><p>Is equipped with a desk, chairs, filing space, a working telephone listed in the name of the dealership, working utilities including electricity and provisions for space heating, and an Internet connection and email address;</p></li><li><p>Displays a sign and business hours as required by this chapter; and</p></li><li><p>Has contiguous space designated for the exclusive use of the dealer adequate to permit the display of at least 10 vehicles.</p></li></ol><p>Any dealer licensed on or before July 1, 1995, shall be considered in compliance with subdivisions 2 and <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0006" target="_blank" rel="noreferrer">6</a> of this section for that licensee.</p><p>1988, c. 865, § 46.1-525.01; 1989, c. 727; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0418" target="_blank" rel="noreferrer">418</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0791" target="_blank" rel="noreferrer">791</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',5),l=[t];function n(o,c,d,h,p,f){return a(),i("div",null,l)}const b=e(r,[["render",n]]);export{g as __pageData,b as default};
