import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-1514 Action on applications; hearing on denial; denial for failure to have established place of business.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1514.md","filePath":"vacode/46.2-1514.md"}'),t={name:"vacode/46.2-1514.md"},r=n('<h1 id="§-46-2-1514-action-on-applications-hearing-on-denial-denial-for-failure-to-have-established-place-of-business" tabindex="-1">§ 46.2-1514 Action on applications; hearing on denial; denial for failure to have established place of business. <a class="header-anchor" href="#§-46-2-1514-action-on-applications-hearing-on-denial-denial-for-failure-to-have-established-place-of-business" aria-label="Permalink to &quot;§ 46.2-1514 Action on applications; hearing on denial; denial for failure to have established place of business.&quot;">​</a></h1><p>The Board shall act on all applications for a license or certificate of dealer registration under this chapter within sixty days after receipt by either granting or refusing the application. Any applicant denied a license or certificate shall, on his written request filed within thirty days, be given a hearing at a time and place determined by the Board or a person designated by the Board. All hearings under this section shall be public and shall be held promptly. The applicant may be represented by counsel.</p><p>Any applicant denied a license for failure to have an established place of business as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-1510/" target="_blank" rel="noreferrer">46.2-1510</a> may not, nor shall anyone, apply for a license for premises for which a license was denied for thirty days from the date of the rejection of the application.</p><p>1988, c. 865, § 46.1-525.05; 1989, c. 727; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>.</p>',4),o=[r];function l(s,c,d,p,h,f){return a(),i("div",null,o)}const g=e(t,[["render",l]]);export{b as __pageData,g as default};
