import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-1527.4 Opportunity to intervene.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1527.4.md","filePath":"vacode/46.2-1527.4.md"}'),o={name:"vacode/46.2-1527.4.md"},n=r('<h1 id="§-46-2-1527-4-opportunity-to-intervene" tabindex="-1">§ 46.2-1527.4 Opportunity to intervene. <a class="header-anchor" href="#§-46-2-1527-4-opportunity-to-intervene" aria-label="Permalink to &quot;§ 46.2-1527.4 Opportunity to intervene.&quot;">​</a></h1><p>Any action instituted by a person against a licensed or registered dealer or a salesperson, which may become a claim against the Fund, shall be served to the Board in the manner prescribed by law. All subsequent pleadings and documents shall also be served to the Board. Included in such service shall be an affidavit stating all acts constituting fraud or violations of this chapter. Upon service of process, the Board, or duly authorized representative, shall have the right to request leave of the court to intervene. The person shall submit such pleadings or documents to the Board by certified mail or the equivalent.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0478" target="_blank" rel="noreferrer">478</a>; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>.</p>',3),i=[n];function s(l,c,d,h,p,_){return t(),a("div",null,i)}const f=e(o,[["render",s]]);export{v as __pageData,f as default};
