import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1532 Odometer disclosure; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1532.md","filePath":"vacode/46.2-1532.md"}'),o={name:"vacode/46.2-1532.md"},i=a('<h1 id="§-46-2-1532-odometer-disclosure-penalty" tabindex="-1">§ 46.2-1532 Odometer disclosure; penalty. <a class="header-anchor" href="#§-46-2-1532-odometer-disclosure-penalty" aria-label="Permalink to &quot;§ 46.2-1532 Odometer disclosure; penalty.&quot;">​</a></h1><p>Every motor vehicle dealer shall comply with all requirements of the Federal Odometer Act and § <a href="http://law.lis.virginia.gov/vacode/46.2-629/" target="_blank" rel="noreferrer">46.2-629</a> by completing the appropriate odometer mileage statement form for each vehicle purchased, sold or transferred, or in any other way acquired or disposed of. Odometer disclosure statements shall be maintained by the dealer in a manner that permits systematic retrieval. Any person found guilty of violating any of the provisions of this section is guilty of a Class 1 misdemeanor.</p><p>The provisions of this section shall not apply to trailers, travel trailers, all-terrain vehicles, or off-road motorcycles.</p><p>1988, c. 865, § 46.1-547.7; 1989, c. 727; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',4),l=[i];function s(n,c,d,p,h,f){return r(),t("div",null,l)}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
