import{_ as e,o as a,c as r,O as s}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-1533 Business hours.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1533.md","filePath":"vacode/46.2-1533.md"}'),t={name:"vacode/46.2-1533.md"},o=s('<h1 id="§-46-2-1533-business-hours" tabindex="-1">§ 46.2-1533 Business hours. <a class="header-anchor" href="#§-46-2-1533-business-hours" aria-label="Permalink to &quot;§ 46.2-1533 Business hours.&quot;">​</a></h1><p>Each motor vehicle dealer shall be open for business a minimum of 20 hours per week, at least 10 of which shall be between the hours of 9:00 a.m. and 5:00 p.m. Monday through Friday, except that the Board, on written request by a dealer, may modify these requirements for good cause. The dealer&#39;s hours shall be posted and maintained conspicuously on or near the main entrance of each place of business.</p><p>Each dealer shall include his business hours on the original and every renewal application for a license, and changes to these hours shall be immediately filed with the Department.</p><p>1988, c. 865, § 46.1-547.8; 1989, c. 727; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',4),n=[o];function i(l,h,c,d,u,p){return a(),r("div",null,n)}const m=e(t,[["render",i]]);export{f as __pageData,m as default};
