import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-1544 Certificate of title for dealers; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1544.md","filePath":"vacode/46.2-1544.md"}'),i={name:"vacode/46.2-1544.md"},o=r('<h1 id="§-46-2-1544-certificate-of-title-for-dealers-penalty" tabindex="-1">§ 46.2-1544 Certificate of title for dealers; penalty. <a class="header-anchor" href="#§-46-2-1544-certificate-of-title-for-dealers-penalty" aria-label="Permalink to &quot;§ 46.2-1544 Certificate of title for dealers; penalty.&quot;">​</a></h1><p>Except as otherwise provided in this chapter, every dealer shall obtain, on the purchase of each vehicle, a certificate of title issued to the dealer or shall obtain an assignment or reassignment of a certificate of title for each vehicle purchased, except that a certificate of title shall not be required for any new vehicle to be sold as such. Any person found guilty of violating any of the provisions of this section is guilty of a Class 1 misdemeanor.</p><p>The provisions of this section shall also apply to watercraft trailers and watercraft trailer dealers.</p><p>1988, c. 865, § 46.1-550.5:2; 1989, c. 727; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',4),l=[o];function s(n,c,f,h,p,d){return t(),a("div",null,l)}const v=e(i,[["render",s]]);export{g as __pageData,v as default};
