import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 46.2-1552.1 Use of dealer's license plates or temporary transport plates for demonstrating trucks or tractor trucks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1552.1.md","filePath":"vacode/46.2-1552.1.md"}`),o={name:"vacode/46.2-1552.1.md"},s=a('<h1 id="§-46-2-1552-1-use-of-dealer-s-license-plates-or-temporary-transport-plates-for-demonstrating-trucks-or-tractor-trucks" tabindex="-1">§ 46.2-1552.1 Use of dealer&#39;s license plates or temporary transport plates for demonstrating trucks or tractor trucks. <a class="header-anchor" href="#§-46-2-1552-1-use-of-dealer-s-license-plates-or-temporary-transport-plates-for-demonstrating-trucks-or-tractor-trucks" aria-label="Permalink to &quot;§ 46.2-1552.1 Use of dealer&#39;s license plates or temporary transport plates for demonstrating trucks or tractor trucks.&quot;">​</a></h1><p>Notwithstanding any other provision of this chapter, dealer&#39;s license plates issued under § <a href="http://law.lis.virginia.gov/vacode/46.2-1548/" target="_blank" rel="noreferrer">46.2-1548</a> and temporary transport plates issued under § <a href="http://law.lis.virginia.gov/vacode/46.2-1550.2/" target="_blank" rel="noreferrer">46.2-1550.2</a> may be used on trucks or tractor trucks in the inventory of licensed motor vehicle dealers for the purpose of demonstrating trucks or tractor trucks in the inventory of a licensed dealer by a bona fide prospective purchaser. Any such demonstration vehicle may be loaded in a manner consistent with the prospective purchaser&#39;s usual commercial activities. Such use of dealer&#39;s license plates on demonstration trucks or tractor trucks in a prospective purchaser&#39;s commercial activities shall be for not more than three days or 750 miles, whichever comes first, and shall not thereafter be used on the same truck or tractor truck by the same prospective purchaser for a period of sixty days. The dealer shall issue to the prospective purchaser, or to his authorized agent, a certificate on forms provided by the Department, a copy of which shall be retained by the dealer and open at all times to the inspection of the Commissioner or any of the officers or agents of the Department. The certificate shall be in the immediate possession of the person operating or authorized to operate the truck or tractor truck. The certificate shall entitle the person to operate with the dealer&#39;s license plate or temporary transport plate for a specific period of no more than three days. This certificate shall be in lieu of any other registration, permit, and motor fuel road tax identification otherwise required by law.</p><p>1993, c. 503; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>.</p>',3),i=[s];function n(c,l,p,h,d,f){return r(),t("div",null,i)}const _=e(o,[["render",n]]);export{m as __pageData,_ as default};
