import{_ as r,o as e,c as a,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-1573.21 Operation of dealership by manufacturer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1573.21.md","filePath":"vacode/46.2-1573.21.md"}'),o={name:"vacode/46.2-1573.21.md"},i=t('<h1 id="§-46-2-1573-21-operation-of-dealership-by-manufacturer" tabindex="-1">§ 46.2-1573.21 Operation of dealership by manufacturer. <a class="header-anchor" href="#§-46-2-1573-21-operation-of-dealership-by-manufacturer" aria-label="Permalink to &quot;§ 46.2-1573.21 Operation of dealership by manufacturer.&quot;">​</a></h1><p>It shall be unlawful for any trailer manufacturer, factory branch, distributor, distributor branch, or subsidiary thereof to own, operate, or control any trailer dealership in the Commonwealth. However, this section shall not prohibit:</p><ol><li><p>The operation by a manufacturer, factory branch, distributor, distributor branch, or subsidiary thereof, of a dealership for a temporary period, not to exceed one year, during the transition from one owner or operator to another;</p></li><li><p>The ownership or control of a dealership by a manufacturer, factory branch, distributor, distributor branch, or subsidiary thereof, while the dealership is being sold under a bona fide contract or purchase option to the operator of the dealership; or</p></li><li><p>The ownership, operation, or control of a dealership by a manufacturer, factory branch, distributor, distributor branch, or subsidiary thereof if the Commissioner determines, after a hearing at the request of any party, that there is no dealer independent of the manufacturer or distributor, factory branch or distributor branch, or subsidiary thereof available in the community or trade area to own and operate the franchise in a manner consistent with the public interest.</p></li></ol><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1043" target="_blank" rel="noreferrer">1043</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1052" target="_blank" rel="noreferrer">1052</a>, § 46.2-1992.73; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',4),n=[i];function s(h,c,l,p,d,f){return e(),a("div",null,n)}const _=r(o,[["render",s]]);export{b as __pageData,_ as default};
