import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-1573.32 Discontinuation of distributors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1573.32.md","filePath":"vacode/46.2-1573.32.md"}'),a={name:"vacode/46.2-1573.32.md"},o=i('<h1 id="§-46-2-1573-32-discontinuation-of-distributors" tabindex="-1">§ 46.2-1573.32 Discontinuation of distributors. <a class="header-anchor" href="#§-46-2-1573-32-discontinuation-of-distributors" aria-label="Permalink to &quot;§ 46.2-1573.32 Discontinuation of distributors.&quot;">​</a></h1><p>If the contract between a distributor and a manufacturer or importer is terminated or otherwise discontinued, all franchises granted to motorcycle dealers in the Commonwealth by that distributor shall continue in full force and shall not be affected by the discontinuance, except that the manufacturer, factory branch, distributor, representative, or other person who undertakes to distribute motorcycles of the same line-make or the same motorcycles of a renamed line-make shall be substituted for the discontinued distributor under the existing motorcycle dealer franchises, and those franchises shall be modified accordingly.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1043" target="_blank" rel="noreferrer">1043</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1052" target="_blank" rel="noreferrer">1052</a>, § 46.2-1993.69; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',3),n=[o];function s(c,d,l,h,f,u){return t(),r("div",null,n)}const m=e(a,[["render",s]]);export{b as __pageData,m as default};
