import{_ as r,o as e,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1573.35 Ownership of service facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1573.35.md","filePath":"vacode/46.2-1573.35.md"}'),o={name:"vacode/46.2-1573.35.md"},a=i('<h1 id="§-46-2-1573-35-ownership-of-service-facilities" tabindex="-1">§ 46.2-1573.35 Ownership of service facilities. <a class="header-anchor" href="#§-46-2-1573-35-ownership-of-service-facilities" aria-label="Permalink to &quot;§ 46.2-1573.35 Ownership of service facilities.&quot;">​</a></h1><p>It shall be unlawful for any motorcycle manufacturer, factory branch, distributor, distributor branch, or subsidiary thereof to own, operate, or control, either directly or indirectly, any motorcycle warranty or service facility located in the Commonwealth. Nothing in this section shall prohibit any motorcycle manufacturer, factory branch, distributor, distributor branch, or subsidiary thereof from owning, operating, or controlling any warranty or service facility for warranty or service of motorcycles owned or operated by the manufacturer, factory branch, distributor, distributor branch, or subsidiary thereof. Nothing contained in this section shall prohibit a motorcycle manufacturer, factory branch, distributor, or distributor branch from performing service for reasons of compliance with an order of a court of competent jurisdiction or of warranty under Chapter 17.3 (§ <a href="http://law.lis.virginia.gov/vacode/59.1-207.9/" target="_blank" rel="noreferrer">59.1-207.9</a> et seq.) of Title 59.1.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1043" target="_blank" rel="noreferrer">1043</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1052" target="_blank" rel="noreferrer">1052</a>, § 46.2-1993.72; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',3),n=[a];function c(s,l,f,h,d,p){return e(),t("div",null,n)}const b=r(o,[["render",c]]);export{_ as __pageData,b as default};
