import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-1573.7 Discontinuation of distributors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1573.7.md","filePath":"vacode/46.2-1573.7.md"}'),a={name:"vacode/46.2-1573.7.md"},o=i('<h1 id="§-46-2-1573-7-discontinuation-of-distributors" tabindex="-1">§ 46.2-1573.7 Discontinuation of distributors. <a class="header-anchor" href="#§-46-2-1573-7-discontinuation-of-distributors" aria-label="Permalink to &quot;§ 46.2-1573.7 Discontinuation of distributors.&quot;">​</a></h1><p>If the contract between a distributor and a manufacturer or importer is terminated or otherwise discontinued, all franchises granted to recreational vehicle dealers in the Commonwealth by that distributor shall continue in full force and shall not be affected by the discontinuance, except that the manufacturer, factory branch, distributor, representative, or other person who undertakes to distribute recreational vehicles of the same line-make or the same recreational vehicles of a renamed line-make shall be substituted for the discontinued distributor under the existing recreational vehicle dealer franchises, and those franchises shall be modified accordingly.</p><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>, § 46.2-1978; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',3),n=[o];function s(c,l,d,h,f,u){return t(),r("div",null,n)}const p=e(a,[["render",s]]);export{b as __pageData,p as default};
