import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-1575 Grounds for denying, suspending, or revoking licenses or certificates of dealer registration or qualification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1575.md","filePath":"vacode/46.2-1575.md"}'),r={name:"vacode/46.2-1575.md"},o=a('<h1 id="§-46-2-1575-grounds-for-denying-suspending-or-revoking-licenses-or-certificates-of-dealer-registration-or-qualification" tabindex="-1">§ 46.2-1575 Grounds for denying, suspending, or revoking licenses or certificates of dealer registration or qualification. <a class="header-anchor" href="#§-46-2-1575-grounds-for-denying-suspending-or-revoking-licenses-or-certificates-of-dealer-registration-or-qualification" aria-label="Permalink to &quot;§ 46.2-1575 Grounds for denying, suspending, or revoking licenses or certificates of dealer registration or qualification.&quot;">​</a></h1><p>A license or certificate of dealer registration or qualification issued under this subtitle may be denied, suspended, or revoked on any one or more of the following grounds:</p><ol><li><p>Material misstatement or omission in application for license, dealer&#39;s license plates, certificate of dealer registration, certificate of qualification, or certificate of title;</p></li><li><p>Failure to comply subsequent to receipt of a written warning from the Department or the Board or any willful failure to comply with any provision of this chapter or any regulation promulgated by the Commissioner or the Board under this chapter;</p></li><li><p>Failure to have an established place of business as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-1510/" target="_blank" rel="noreferrer">46.2-1510</a> or failure to have as the dealer-operator an individual who holds a valid certificate of qualification;</p></li><li><p>Defrauding any retail buyer, to the buyer&#39;s damage, or any other person in the conduct of the licensee&#39;s or registrant&#39;s business;</p></li><li><p>Employment of fraudulent devices, methods or practices in connection with compliance with the requirements under the statutes of the Commonwealth with respect to the retaking of vehicles under retail installment contracts and the redemption and resale of those vehicles;</p></li><li><p>Having used deceptive acts or practices;</p></li><li><p>Knowingly advertising by any means any assertion, representation, or statement of fact which is untrue, misleading, or deceptive in any particular relating to the conduct of the business licensed or registered or for which a license or registration is sought;</p></li><li><p>Having been convicted of any fraudulent act in connection with the business of selling vehicles or any consumer-related fraud;</p></li><li><p>Having been convicted of any criminal act involving the business of selling vehicles;</p></li><li><p>Willfully retaining in his possession title to a motor vehicle that has not been completely and legally assigned to him;</p></li><li><p>Failure to comply with any provision of Chapter 4.1 (§ <a href="http://law.lis.virginia.gov/vacode/36-85.2/" target="_blank" rel="noreferrer">36-85.2</a> et seq.) of Title 36 or any regulation promulgated pursuant to that chapter;</p></li><li><p>Leasing, renting, lending, or otherwise allowing the use of a dealer&#39;s license plate by persons not specifically authorized under this title;</p></li><li><p>Having been convicted of a felony;</p></li><li><p>Failure to submit to the Department, within thirty days from the date of sale, any application, tax, or fee collected for the Department on behalf of a buyer;</p></li><li><p>Having been convicted of larceny of a vehicle or receipt or sale of a stolen vehicle;</p></li><li><p>Having been convicted of odometer tampering or any related violation;</p></li><li><p>If a salvage dealer, salvage pool, or rebuilder, failing to comply with any provision of Chapter 16 (§ <a href="http://law.lis.virginia.gov/vacode/46.2-1600/" target="_blank" rel="noreferrer">46.2-1600</a> et seq.) of this title or any regulation promulgated by the Commissioner under that chapter;</p></li><li><p>Failing to maintain automobile liability insurance, issued by a company licensed to do business in the Commonwealth, or a certificate of self-insurance as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-368/" target="_blank" rel="noreferrer">46.2-368</a>, with respect to each dealer&#39;s license plate issued to the dealer by the Department; or</p></li><li><p>Failing or refusing to pay civil penalties imposed by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/46.2-1507/" target="_blank" rel="noreferrer">46.2-1507</a>.</p></li></ol><p>1988, c. 865, § 46.1-550.5:34; 1989, c. 727; 1990, cc. 197, 954; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0217" target="_blank" rel="noreferrer">217</a>.</p>',4),n=[o];function l(s,c,p,d,f,h){return i(),t("div",null,n)}const v=e(r,[["render",l]]);export{u as __pageData,v as default};
