import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-1582 Enforcement; regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1582.md","filePath":"vacode/46.2-1582.md"}'),o={name:"vacode/46.2-1582.md"},n=r('<h1 id="§-46-2-1582-enforcement-regulations" tabindex="-1">§ 46.2-1582 Enforcement; regulations. <a class="header-anchor" href="#§-46-2-1582-enforcement-regulations" aria-label="Permalink to &quot;§ 46.2-1582 Enforcement; regulations.&quot;">​</a></h1><p>A. The Board may promulgate regulations reasonably necessary for enforcement of this article.</p><p>B. In addition to any other sanctions or remedies available to the Board under this chapter, the Board may assess a civil penalty not to exceed $1,000 for any single violation of this article. Each day that a violation continues shall constitute a separate violation.</p><p>C. The authority granted in this article shall be in addition to and not a substitute for the powers and authority granted pursuant to the provisions of the Virginia Consumer Protection Act (§ <a href="/vacode/59.1-196/">59.1-196</a> et seq.).</p><p>1989, c. 308, § 46.1-550.5:41; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',5),i=[n];function s(c,l,h,d,p,_){return t(),a("div",null,i)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
