import{_ as e,o as i,c as o,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-1601.3 Grounds for denying, suspending, or revoking licenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1601.3.md","filePath":"vacode/46.2-1601.3.md"}'),r={name:"vacode/46.2-1601.3.md"},t=n('<h1 id="§-46-2-1601-3-grounds-for-denying-suspending-or-revoking-licenses" tabindex="-1">§ 46.2-1601.3 Grounds for denying, suspending, or revoking licenses. <a class="header-anchor" href="#§-46-2-1601-3-grounds-for-denying-suspending-or-revoking-licenses" aria-label="Permalink to &quot;§ 46.2-1601.3 Grounds for denying, suspending, or revoking licenses.&quot;">​</a></h1><p>The Commissioner may deny, suspend, or revoke a license under this chapter on any one or more of the following grounds:</p><ol><li><p>Material misstatement or omission in application for license, certificate of title, salvage certificate, or nonrepairable certificate;</p></li><li><p>Failure to comply subsequent to receipt of a written warning from the Commissioner;</p></li><li><p>Failure to comply with the requirements of subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-1601/" target="_blank" rel="noreferrer">46.2-1601</a>;</p></li><li><p>Defrauding any retail buyer, to the buyer&#39;s damage, or any other person in the conduct of the licensee&#39;s business;</p></li><li><p>Having used deceptive acts or practices;</p></li><li><p>Knowingly advertising by any means any assertion, representation, or statement of fact which is untrue, misleading, or deceptive in any particular relating to the conduct of the business licensed for which a license is sought;</p></li><li><p>Having been convicted of any fraudulent act in connection with the business of selling vehicles, vehicle parts, or major components;</p></li><li><p>Having been convicted of any criminal act involving the business of selling vehicles, vehicle parts, or major components;</p></li><li><p>Willfully retaining in his possession title to a motor vehicle or a salvage certificate that has not been completely and legally assigned to him;</p></li><li><p>Having been convicted of a felony;</p></li><li><p>Having been convicted of larceny of a vehicle or receipt or sale of a stolen vehicle;</p></li><li><p>Having been convicted of odometer tampering or any related violation;</p></li><li><p>Having been convicted of a violation of § <a href="http://law.lis.virginia.gov/vacode/46.2-1074/" target="_blank" rel="noreferrer">46.2-1074</a> or <a href="http://law.lis.virginia.gov/vacode/46.2-1075/" target="_blank" rel="noreferrer">46.2-1075</a>;</p></li><li><p>Failure to comply with federal reporting requirements pursuant to subsection G of § <a href="http://law.lis.virginia.gov/vacode/46.2-1603.1/" target="_blank" rel="noreferrer">46.2-1603.1</a>;</p></li><li><p>Failure or refusal to pay civil penalties imposed by the Commissioner pursuant to § <a href="http://law.lis.virginia.gov/vacode/46.2-1609/" target="_blank" rel="noreferrer">46.2-1609</a>;</p></li><li><p>Failure to comply with the requirements of § <a href="http://law.lis.virginia.gov/vacode/46.2-1205/" target="_blank" rel="noreferrer">46.2-1205</a>;</p></li><li><p>Failure to comply with the requirements of § <a href="http://law.lis.virginia.gov/vacode/46.2-1608.2/" target="_blank" rel="noreferrer">46.2-1608.2</a>; or</p></li><li><p>Failure to comply with any other provision of this chapter.</p></li></ol><p>Suspension or revocation under this section shall only be imposed on the specific business found to be in violation.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0058" target="_blank" rel="noreferrer">58</a>.</p>',5),a=[t];function l(s,c,p,h,d,f){return i(),o("div",null,a)}const u=e(r,[["render",l]]);export{v as __pageData,u as default};
