import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1602 Certain sales prohibited; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1602.md","filePath":"vacode/46.2-1602.md"}'),i={name:"vacode/46.2-1602.md"},r=t('<h1 id="§-46-2-1602-certain-sales-prohibited-exceptions" tabindex="-1">§ 46.2-1602 Certain sales prohibited; exceptions. <a class="header-anchor" href="#§-46-2-1602-certain-sales-prohibited-exceptions" aria-label="Permalink to &quot;§ 46.2-1602 Certain sales prohibited; exceptions.&quot;">​</a></h1><p>A. It shall be unlawful:</p><ol><li><p>For any scrap metal processor to sell a vehicle or vehicle components or parts;</p></li><li><p>For any salvage pool to sell either in person or through any Internet auction a salvage vehicle stored in the Commonwealth to any person who is not licensed as an auto recycler, motor vehicle dealer, or vehicle removal operator by the Commonwealth or regulated as a similar business under the laws of another state;</p></li><li><p>For any person to sell a nonrepairable vehicle to any person who is not licensed as an auto recycler or vehicle removal operator by the Commonwealth or regulated as a similar business under the laws of another state; or</p></li><li><p>For any person to sell a rebuilt vehicle without first having disclosed the fact that the vehicle is a rebuilt vehicle to the buyer in writing on a form prescribed by the Commissioner.</p></li></ol><p>B. Notwithstanding the provisions of subsection A of this section, it shall not be unlawful:</p><ol><li><p>For a salvage dealer to sell vehicle components or parts to unlicensed persons; or</p></li><li><p>For an individual to dispose of a salvage vehicle acquired or retained for his own use when it has been acquired or retained and used in good faith and not for the purpose of avoiding the provisions of this chapter.</p></li></ol><p>1979, c. 401, § 46.1-550.6; 1988, cc. 842, 865; 1989, c. 727; 1992, c. 148; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0033" target="_blank" rel="noreferrer">33</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0177" target="_blank" rel="noreferrer">177</a>.</p>',6),s=[r];function n(l,c,h,p,d,u){return o(),a("div",null,s)}const f=e(i,[["render",n]]);export{_ as __pageData,f as default};
