import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-1707 Unlawful acts; prosecution; proceedings in equity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1707.md","filePath":"vacode/46.2-1707.md"}'),o={name:"vacode/46.2-1707.md"},r=n('<h1 id="§-46-2-1707-unlawful-acts-prosecution-proceedings-in-equity" tabindex="-1">§ 46.2-1707 Unlawful acts; prosecution; proceedings in equity. <a class="header-anchor" href="#§-46-2-1707-unlawful-acts-prosecution-proceedings-in-equity" aria-label="Permalink to &quot;§ 46.2-1707 Unlawful acts; prosecution; proceedings in equity.&quot;">​</a></h1><p>A. It shall be unlawful for any person to engage in any of the following acts:</p><ol><li><p>Practicing as a driver training school or as an instructor without holding a valid license as required by statute or regulation;</p></li><li><p>Making use of any designation provided by statute or regulation to denote a standard of professional or occupational competence without being duly certified or licensed;</p></li><li><p>Performing any act or function which is restricted by statute or regulation to persons holding a driver training school or instructor license or certification, without being duly certified or licensed;</p></li><li><p>Materially misrepresenting facts in an application for licensure, certification or registration;</p></li><li><p>Willfully refusing to furnish the Department information or records required or requested pursuant to statute or regulation; or</p></li><li><p>Violating any statute or regulation governing the practice of any driver training school or instructor regulated pursuant to this chapter.</p></li></ol><p>Any person who willfully engages in any unlawful act enumerated in this section shall be guilty of a Class 1 misdemeanor. However, the third or any subsequent conviction for violating this section during a 36-month period shall constitute a Class 6 felony.</p><p>B. In addition to the provisions of subsection A of this section, the Department may institute proceedings in equity to enjoin any person from engaging in any unlawful act enumerated in this section. Such proceedings shall be brought in the name of the Commonwealth in the circuit court of the city or county in which the unlawful act occurred or in which the defendant resides.</p><p>1991, c. 214; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0587" target="_blank" rel="noreferrer">587</a>.</p>',6),a=[r];function s(c,l,u,p,d,h){return t(),i("div",null,a)}const y=e(o,[["render",s]]);export{g as __pageData,y as default};
