import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-200 Department of Motor Vehicles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-200.md","filePath":"vacode/46.2-200.md"}'),i={name:"vacode/46.2-200.md"},o=a('<h1 id="§-46-2-200-department-of-motor-vehicles" tabindex="-1">§ 46.2-200 Department of Motor Vehicles. <a class="header-anchor" href="#§-46-2-200-department-of-motor-vehicles" aria-label="Permalink to &quot;§ 46.2-200 Department of Motor Vehicles.&quot;">​</a></h1><p>There shall be a Department of Motor Vehicles in the executive department, responsible to the Secretary of Transportation. The Department shall be under the supervision and management of the Commissioner of the Department of Motor Vehicles.</p><p>The Department shall be responsible for the administration of the motor vehicle license, registration and title laws; the issuance, suspension, and revocation of driver&#39;s licenses; the examination of applicants for and holders of driver&#39;s licenses; the administration, training, disciplining, and assignment of examiners of applicants for driver&#39;s licenses; the administration of the safety responsibility laws, fuel tax laws, the provisions of this title relating to transportation safety, and dealer licensing laws; the registration of carriers of passengers or property and vehicles that may be required to be registered under the International Registration Plan or pay road tax as described under Chapter 27 (§ <a href="http://law.lis.virginia.gov/vacode/58.1-2700/" target="_blank" rel="noreferrer">58.1-2700</a> et seq.) of Title 58.1 under the International Fuel Tax Agreement; the audit of carriers of passengers or property for compliance with registration and road tax requirements; proof of financial responsibility; and any other services that may be required to create a single point of contact for motor carriers operating within and without the Commonwealth, including the operation of permanent and mobile motor carrier service centers.</p><p>Code 1950, § 46-26; 1958, c. 541, § 46.1-25; 1984, cc. 778, 780; 1989, c. 727; 1990, cc. 1, 317; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0070" target="_blank" rel="noreferrer">70</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0082" target="_blank" rel="noreferrer">82</a>.</p>',4),n=[o];function s(l,c,h,p,f,d){return t(),r("div",null,n)}const _=e(i,[["render",s]]);export{m as __pageData,_ as default};
