import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-2000.1 Vehicles excluded from operation of chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2000.1.md","filePath":"vacode/46.2-2000.1.md"}'),i={name:"vacode/46.2-2000.1.md"},a=o('<h1 id="§-46-2-2000-1-vehicles-excluded-from-operation-of-chapter" tabindex="-1">§ 46.2-2000.1 Vehicles excluded from operation of chapter. <a class="header-anchor" href="#§-46-2-2000-1-vehicles-excluded-from-operation-of-chapter" aria-label="Permalink to &quot;§ 46.2-2000.1 Vehicles excluded from operation of chapter.&quot;">​</a></h1><p>This chapter shall not be construed to include:</p><ol><li><p>Motor vehicles employed solely in transporting school children and teachers;</p></li><li><p>Taxicabs, or other motor vehicles performing bona fide taxicab service, having a seating capacity of not more than six passengers, excluding the driver, while operating in a county, city, or town which has or adopts an ordinance regulating and controlling taxicabs and other vehicles performing a bona fide taxicab service, and not operating on a regular route or between fixed termini;</p></li><li><p>Motor vehicles owned or operated by or on behalf of hotels while used exclusively for the transportation of hotel patronage between hotels and local railroad or other common carrier stations;</p></li><li><p>Motor vehicles owned and operated by the United States, the District of Columbia, or any state, or any municipality or any other political subdivision of this Commonwealth, including passenger-carrying motor vehicles while being operated under an exclusive contract with the United States;</p></li><li><p>Any motor vehicle designed with a seating capacity for and used to transport not more than 15 passengers, including the driver, if the driver and the passengers are engaged in a share-the-ride undertaking and if they share not more than the expenses of operation of the vehicle. Regular payments toward a capital recovery fund not exceeding the cost of the vehicle or used to pay for leasing the vehicle are to be considered eligible expenses of operation;</p></li><li><p>Unless otherwise provided, motor vehicles while used exclusively in the transportation of passengers within the corporate limits of incorporated cities or towns, and motor vehicles used exclusively in the regular transportation of passengers within the boundaries of such cities or towns and adjacent counties where such vehicles are being operated by such county or pursuant to a contract with the board of supervisors of such county;</p></li><li><p>Motor vehicles while operated under the exclusive regulatory control of a transportation district commission acting pursuant to the Transportation District Act of 1964 (§ <a href="http://law.lis.virginia.gov/vacode/33.2-1900/" target="_blank" rel="noreferrer">33.2-1900</a> et seq.) of Title 33.2;</p></li><li><p>Motor vehicles used for the transportation of passengers by nonprofit, nonstock corporations funded solely by federal, state or local subsidies, the use of which motor vehicles are restricted as to regular and irregular routes to contracts with four or more counties and, at the commencement of the operation, no certificated carrier provides the same or similar services within such counties; and</p></li><li><p>Emergency medical services vehicles as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-111.1/" target="_blank" rel="noreferrer">32.1-111.1</a>.</p></li></ol><p>Code 1950, § 56-274; 1950, p. 370; 1958, c. 285; 1966, c. 575; 1968, c. 183; 1970, c. 33; 1973, c. 460; 1975, c. 122; 1976, cc. 378, 411; 1977, c. 514; 1978, c. 152; 1979, cc. 608, 618; 1980, c. 230; 1982, c. 257; 1983, c. 112; 1985, c. 88; 1986, c. 420; 1989, c. 625; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0337" target="_blank" rel="noreferrer">337</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',4),n=[a];function c(s,l,h,p,d,f){return r(),t("div",null,n)}const v=e(i,[["render",c]]);export{u as __pageData,v as default};
