import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 46.2-2001.3 Application; notice requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2001.3.md","filePath":"vacode/46.2-2001.3.md"}'),a={name:"vacode/46.2-2001.3.md"},n=t('<h1 id="§-46-2-2001-3-application-notice-requirements" tabindex="-1">§ 46.2-2001.3 Application; notice requirements. <a class="header-anchor" href="#§-46-2-2001-3-application-notice-requirements" aria-label="Permalink to &quot;§ 46.2-2001.3 Application; notice requirements.&quot;">​</a></h1><p>A. Applications for a license, permit, certificate, or identification marker or renewal of a license, permit, certificate, or identification marker under this chapter shall be made to the Department and contain such information and exhibits as the Department shall require. Such information shall include, in the application or otherwise, the matters set forth in § <a href="http://law.lis.virginia.gov/vacode/46.2-2011.24/" target="_blank" rel="noreferrer">46.2-2011.24</a> as grounds for denying licenses, permits, and certificates, and other pertinent matters requisite for the safeguarding of the public interest.</p><p>Notwithstanding any other provision of this chapter, the Commissioner may require all or certain applications for a license, permit, certificate, or identification marker to be filed electronically.</p><p>B. An applicant for any original certificate of public convenience and necessity issued under this chapter, or any request for a transfer of such certificate, unless otherwise provided, shall cause a notice of such application, on the form and in the manner prescribed by the Department, on every motor carrier holding the same type of certificate issued by the Department and operating or providing service within the area proposed to be served by the applicant.</p><p>C. For any application for original certificate or license issued under this chapter, or any request for a transfer of such certificate or license, the Department shall publish a notice of such application on the Department&#39;s public website in the form and in the manner prescribed by the Department.</p><p>D. An applicant for any original certificate of public convenience and necessity issued under this chapter, or any request for a transfer of such certificate of public convenience and necessity, shall cause a publication of a summary of the application to be made in a newspaper having a general circulation in the proposed area to be served or area where the primary business office is located within such time as the Department may prescribe.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0870" target="_blank" rel="noreferrer">870</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0022" target="_blank" rel="noreferrer">22</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0111" target="_blank" rel="noreferrer">111</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0165" target="_blank" rel="noreferrer">165</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0582" target="_blank" rel="noreferrer">582</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0694" target="_blank" rel="noreferrer">694</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0708" target="_blank" rel="noreferrer">708</a>.</p>',7),o=[n];function c(l,s,p,f,h,g){return r(),i("div",null,o)}const m=e(a,[["render",c]]);export{d as __pageData,m as default};
