import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-2011.1 Issuance of temporary authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2011.1.md","filePath":"vacode/46.2-2011.1.md"}'),o={name:"vacode/46.2-2011.1.md"},i=a('<h1 id="§-46-2-2011-1-issuance-of-temporary-authority" tabindex="-1">§ 46.2-2011.1 Issuance of temporary authority. <a class="header-anchor" href="#§-46-2-2011-1-issuance-of-temporary-authority" aria-label="Permalink to &quot;§ 46.2-2011.1 Issuance of temporary authority.&quot;">​</a></h1><p>To enable the provision of service for which there is an immediate and urgent need to a point or between points in Virginia where certificated carriers are unable to perform the service, or within a territory having no certificated carrier, the Department may, in its discretion and without hearings or other proceedings, grant temporary authority for such service by a carrier that would otherwise be required to obtain a certificate under this chapter. Such temporary authority, unless suspended or revoked in accordance with § <a href="http://law.lis.virginia.gov/vacode/46.2-2011.26/" target="_blank" rel="noreferrer">46.2-2011.26</a>, shall be valid for such time as the Department shall specify, but for not more than an aggregate of 180 days, and shall create no presumption that corresponding permanent authority will be granted thereafter.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0170" target="_blank" rel="noreferrer">170</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>.</p>',3),n=[i];function s(c,h,p,d,l,u){return r(),t("div",null,n)}const m=e(o,[["render",s]]);export{_ as __pageData,m as default};
