import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-2011.11 Established place of business.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2011.11.md","filePath":"vacode/46.2-2011.11.md"}'),i={name:"vacode/46.2-2011.11.md"},o=r('<h1 id="§-46-2-2011-11-established-place-of-business" tabindex="-1">§ 46.2-2011.11 Established place of business. <a class="header-anchor" href="#§-46-2-2011-11-established-place-of-business" aria-label="Permalink to &quot;§ 46.2-2011.11 Established place of business.&quot;">​</a></h1><p>A. No license or certificate shall be issued to any applicant that does not have an established place of business, owned or leased by the applicant, where a substantial portion of the activity of the motor carrier, TNC broker, or broker business will be routinely conducted and that:</p><ol><li><p>Satisfies all applicable local zoning regulations;</p></li><li><p>Houses all records that the motor carrier, TNC broker, or broker is required to maintain by this chapter or by regulations promulgated pursuant to this chapter; and</p></li><li><p>Is equipped with a working telephone listed or advertised in the name of the motor carrier, TNC broker, or broker.</p></li></ol><p>B. Every licensee and certificate holder shall maintain an established place of business in accordance with subsection A of this section and keep on file a physical address with the Department. Every licensee and certificate holder shall inform the Department by certified letter or other manner prescribed by the Department of any changes to the motor carrier, TNC broker, or broker&#39;s mailing address, physical location, telephone number, and legal status, legal name of company, or trade name of company within 30 days of such change.</p><p>C. Any licensee or certificate holder that relocates his established place of business shall confirm to the Department that the new established place of business conforms to the requirements of subsection A.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0022" target="_blank" rel="noreferrer">22</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0111" target="_blank" rel="noreferrer">111</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0165" target="_blank" rel="noreferrer">165</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0582" target="_blank" rel="noreferrer">582</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0635" target="_blank" rel="noreferrer">635</a>.</p>',6),s=[o];function n(l,c,h,p,d,f){return a(),t("div",null,s)}const u=e(i,[["render",n]]);export{g as __pageData,u as default};
