import{_ as a,o as r,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 46.2-2011.15 Department may seek judgment for refunds due public and collect and distribute same.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2011.15.md","filePath":"vacode/46.2-2011.15.md"}'),o={name:"vacode/46.2-2011.15.md"},i=e("h1",{id:"§-46-2-2011-15-department-may-seek-judgment-for-refunds-due-public-and-collect-and-distribute-same",tabindex:"-1"},[t("§ 46.2-2011.15 Department may seek judgment for refunds due public and collect and distribute same. "),e("a",{class:"header-anchor",href:"#§-46-2-2011-15-department-may-seek-judgment-for-refunds-due-public-and-collect-and-distribute-same","aria-label":'Permalink to "§ 46.2-2011.15 Department may seek judgment for refunds due public and collect and distribute same."'},"​")],-1),s=e("p",null,"If any motor carrier or broker, upon the final decision of an appeal from the action of the Department prescribing rates, charges, tariffs, or classification of traffic, confirming or modifying the action of the Department, fails to refund in the manner and within the time prescribed in the notice of the Department all amounts that the appealing carrier or broker may have collected, pending the appeal, in excess of that authorized by such final decision, upon notice to such carrier or broker by the Department of such final decision, then the Department, after thirty days' notice to any such carrier or broker, may, unless the amount required by such final decision is paid to the Department, seek judgment in the name of the Commonwealth, for the use of the persons, firms and corporations entitled to the same, against any such carrier or broker for the aggregate amount of such collections and for costs, and may enforce the amount of such judgment and costs by process of execution, as provided by law. The Department shall, upon the collection of such judgment, forthwith distribute the amount thereof among the parties entitled thereto, respectively, in such manner as it may by its rules or regulations prescribe, and shall, upon the payment or collection of any such judgment, mark the same satisfied upon its records, and have the same entered satisfied on the judgment lien docket of the court where the same may have been docketed; the satisfaction of any such judgment shall be a bar to any further action or recovery against any such carrier or broker to the extent of such recovery.",-1),c=e("p",null,[t("2001, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596",target:"_blank",rel:"noreferrer"},"596"),t("; 2002, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0861",target:"_blank",rel:"noreferrer"},"861"),t(".")],-1),d=[i,s,c];function h(l,u,m,f,p,b){return r(),n("div",null,d)}const _=a(o,[["render",h]]);export{y as __pageData,_ as default};
