import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-2011.24 Grounds for denying, suspending, or revoking licenses, permits, or certificates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2011.24.md","filePath":"vacode/46.2-2011.24.md"}'),n={name:"vacode/46.2-2011.24.md"},o=t('<h1 id="§-46-2-2011-24-grounds-for-denying-suspending-or-revoking-licenses-permits-or-certificates" tabindex="-1">§ 46.2-2011.24 Grounds for denying, suspending, or revoking licenses, permits, or certificates. <a class="header-anchor" href="#§-46-2-2011-24-grounds-for-denying-suspending-or-revoking-licenses-permits-or-certificates" aria-label="Permalink to &quot;§ 46.2-2011.24 Grounds for denying, suspending, or revoking licenses, permits, or certificates.&quot;">​</a></h1><p>A license, permit, or certificate issued pursuant to this chapter may be denied, suspended, or revoked on any one or more of the following grounds, where applicable:</p><ol><li><p>Material misstatement or omission in application for license, certificate, permit, identification marker, or vehicle registration;</p></li><li><p>Failure to comply subsequent to receipt of a written warning from the Department or any willful failure to comply with a lawful order, any provision of this chapter or any regulation promulgated by the Department under this chapter, or any term, condition, or restriction of a license, permit, or certificate;</p></li><li><p>Failure to comply with zoning or other land use regulations, ordinances, or statutes;</p></li><li><p>Use of deceptive business acts or practices;</p></li><li><p>Knowingly advertising by any means any assertion, representation, or statement of fact that is untrue, misleading, or deceptive relating to the conduct of the business for which a license, certificate, permit, identification marker, or vehicle registration is held or sought;</p></li><li><p>Having been found, through a judicial or administrative hearing, to have committed fraudulent or deceptive acts in connection with the business for which a license, permit, or certificate is held or sought or any consumer-related fraud;</p></li><li><p>Having been convicted of any criminal act involving the business for which a license, permit, or certificate is held or sought;</p></li><li><p>Failure to comply with § <a href="http://law.lis.virginia.gov/vacode/46.2-2056/" target="_blank" rel="noreferrer">46.2-2056</a> or any regulation promulgated pursuant thereto;</p></li><li><p>Improper leasing, renting, lending, or otherwise allowing the improper use of a license, certificate, permit, identification marker, or vehicle registration;</p></li><li><p>Having been convicted of a felony;</p></li><li><p>Having been convicted of any misdemeanor involving lying, cheating, stealing, or moral turpitude;</p></li><li><p>Failure to submit to the Department any tax, fees, dues, fines, or penalties owed to the Department;</p></li><li><p>Failure to furnish the Department information, documentation, or records required or requested pursuant to statute or regulation;</p></li><li><p>Knowingly and willfully filing any false report, account, record, or memorandum;</p></li><li><p>Failure to meet or maintain application certifications or requirements of public convenience and necessity, character, fitness, and financial responsibility pursuant to this chapter;</p></li><li><p>Willfully altering or changing the appearance or wording of any license, permit, certificate, identification marker, license plate, or vehicle registration;</p></li><li><p>Failure to provide services in accordance with license, permit, or certificate terms, limitations, conditions, or requirements;</p></li><li><p>Failure to maintain and keep on file with the Department motor carrier liability insurance, issued by a company licensed to do business in the Commonwealth, or a bond, certificate of insurance, certificate of self-insurance, or unconditional letter of credit in accordance with this chapter, with respect to each motor vehicle operated in the Commonwealth;</p></li><li><p>Failure to comply with the Workers&#39; Compensation Act of Title 65.2;</p></li><li><p>Failure to properly register a motor vehicle under this title;</p></li><li><p>Failure to comply with any federal motor carrier statute, rule, or regulation;</p></li><li><p>Failure to comply with the requirements of the Americans with Disabilities Act or the Virginians with Disabilities Act (§ <a href="http://law.lis.virginia.gov/vacode/51.5-1/" target="_blank" rel="noreferrer">51.5-1</a> et seq.);</p></li><li><p>Inactivity of a motor carrier as may be evidenced by the absence of a motor vehicle registered to operate under such certificate or permit for a period of greater than three months; or</p></li><li><p>Failure to comply with any provision regarding the filing and registered agent requirements set forth in Title 13.1.</p></li></ol><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0165" target="_blank" rel="noreferrer">165</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0582" target="_blank" rel="noreferrer">582</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>.</p>',4),a=[o];function l(s,c,p,h,d,u){return i(),r("div",null,a)}const m=e(n,[["render",l]]);export{g as __pageData,m as default};
