import{_ as e,o as r,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-2011.29 Surrender of identification marker, license plate, and registration card; removal by law enforcement; operation of vehicle denied.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2011.29.md","filePath":"vacode/46.2-2011.29.md"}'),t={name:"vacode/46.2-2011.29.md"},n=a('<h1 id="§-46-2-2011-29-surrender-of-identification-marker-license-plate-and-registration-card-removal-by-law-enforcement-operation-of-vehicle-denied" tabindex="-1">§ 46.2-2011.29 Surrender of identification marker, license plate, and registration card; removal by law enforcement; operation of vehicle denied. <a class="header-anchor" href="#§-46-2-2011-29-surrender-of-identification-marker-license-plate-and-registration-card-removal-by-law-enforcement-operation-of-vehicle-denied" aria-label="Permalink to &quot;§ 46.2-2011.29 Surrender of identification marker, license plate, and registration card; removal by law enforcement; operation of vehicle denied.&quot;">​</a></h1><p>A. It shall be unlawful for a licensee, permittee, or certificate holder whose license, permit, or certificate has expired or been revoked, suspended, or canceled or whose renewal thereof has been denied pursuant to this chapter to fail or refuse to surrender, on demand, to the Department license plates, identification markers, and registration cards issued under this title.</p><p>B. It shall be unlawful for a vehicle owner who is not the holder of a valid permit or certificate or whose vehicle is not validly leased to a motor carrier holding an active permit or certificate to fail or refuse to surrender to the Department on demand license plates, identification markers, and registration cards issued under this title.</p><p>C. If any law-enforcement officer finds that a vehicle bearing Virginia license plates or temporary transport plates is in violation of subsection A or B, such law-enforcement officer may remove the license plate, identification marker, and registration card. If a law-enforcement officer removes a license plate, identification marker, or registration card, he shall forward the same to the Department.</p><p>D. When informed that a vehicle is being operated in violation of this section, the driver shall drive the vehicle to a nearby location off the public highways and not remove it or allow it to be moved until the motor carrier is in compliance with all provisions of this chapter.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0694" target="_blank" rel="noreferrer">694</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0708" target="_blank" rel="noreferrer">708</a>.</p>',6),o=[n];function l(c,s,d,f,h,p){return r(),i("div",null,o)}const v=e(t,[["render",l]]);export{g as __pageData,v as default};
