import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-2011.5 Filing and application fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2011.5.md","filePath":"vacode/46.2-2011.5.md"}'),r={name:"vacode/46.2-2011.5.md"},n=i('<h1 id="§-46-2-2011-5-filing-and-application-fees" tabindex="-1">§ 46.2-2011.5 Filing and application fees. <a class="header-anchor" href="#§-46-2-2011-5-filing-and-application-fees" aria-label="Permalink to &quot;§ 46.2-2011.5 Filing and application fees.&quot;">​</a></h1><p>A. Unless otherwise provided, every applicant, other than a transportation network company, for an original license, permit, or certificate issued under this chapter and transfer of a license or certificate under the provisions of this chapter shall, upon the filing of an application, deposit with the Department, as a filing fee, a sum in the amount of $50.</p><p>B. An applicant for a certificate under § <a href="http://law.lis.virginia.gov/vacode/46.2-2099.45/" target="_blank" rel="noreferrer">46.2-2099.45</a> shall elect and remit to the Department one of the following fees:</p><ol><li><p>An annual fee of $100,000 to accompany an application for an original certificate or a fee of $60,000 to accompany an application for renewal thereof; or</p></li><li><p>A fee of $20 per report to accompany payment for each driving history research report the applicant obtains from the Department pursuant to subdivision B 2 of § <a href="http://law.lis.virginia.gov/vacode/46.2-2099.49/" target="_blank" rel="noreferrer">46.2-2099.49</a>, which fee shall be in addition to any other fees that are authorized for such reports.</p></li></ol><p>A transportation network company may change its election under this subsection when applying for renewal of its certificate.</p><p>If the Department does not approve an application for an original certificate, the Department shall refund to the applicant $90,000 of the application fee paid under subdivision 1.</p><p>C. The Department shall collect a fee of $3 for the issuance of a duplicate license, permit, or certificate issued under this chapter.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0074" target="_blank" rel="noreferrer">74</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0126" target="_blank" rel="noreferrer">126</a>.</p>',8),o=[n];function l(p,c,f,s,h,d){return a(),t("div",null,o)}const u=e(r,[["render",l]]);export{_ as __pageData,u as default};
