import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-2011.6 Vehicle fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2011.6.md","filePath":"vacode/46.2-2011.6.md"}'),i={name:"vacode/46.2-2011.6.md"},o=t('<h1 id="§-46-2-2011-6-vehicle-fees" tabindex="-1">§ 46.2-2011.6 Vehicle fees. <a class="header-anchor" href="#§-46-2-2011-6-vehicle-fees" aria-label="Permalink to &quot;§ 46.2-2011.6 Vehicle fees.&quot;">​</a></h1><p>Every person, other than a TNC partner, who operates a passenger vehicle for compensation over the highways of the Commonwealth, unless such operation is exempted from this chapter, shall be required to pay an annual fee of $3 for each such vehicle so operated, unless a vehicle identification marker fee has been paid to the Department as to such vehicle for the current year under the provisions of Chapter 27 (§ <a href="http://law.lis.virginia.gov/vacode/58.1-2700/" target="_blank" rel="noreferrer">58.1-2700</a> et seq.) of Title 58.1. Such fee shall be paid through the single state registration system established pursuant to 49 U.S.C. § 14504 and 49 C.F.R. Part 367 or through the unified carrier registration system established pursuant to 49 U.S.C. § 14504a and the federal regulations promulgated thereunder for carriers registered pursuant to those provisions. No more than one vehicle fee shall be charged or paid as to any vehicle in any one year under Chapter 27 (§ <a href="http://law.lis.virginia.gov/vacode/58.1-2700/" target="_blank" rel="noreferrer">58.1-2700</a> et seq.) of Title 58.1 and this chapter, including payments made pursuant to the single state registration system or the unified carrier registration system.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0322" target="_blank" rel="noreferrer">322</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0208" target="_blank" rel="noreferrer">208</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>.</p>',3),n=[o];function s(h,l,c,p,f,g){return r(),a("div",null,n)}const _=e(i,[["render",s]]);export{u as __pageData,_ as default};
