import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-2011.8 Transfers of certificates of public convenience and necessity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2011.8.md","filePath":"vacode/46.2-2011.8.md"}'),n={name:"vacode/46.2-2011.8.md"},i=t('<h1 id="§-46-2-2011-8-transfers-of-certificates-of-public-convenience-and-necessity" tabindex="-1">§ 46.2-2011.8 Transfers of certificates of public convenience and necessity. <a class="header-anchor" href="#§-46-2-2011-8-transfers-of-certificates-of-public-convenience-and-necessity" aria-label="Permalink to &quot;§ 46.2-2011.8 Transfers of certificates of public convenience and necessity.&quot;">​</a></h1><p>Any certificate of public convenience and necessity issued under this chapter may be transferred, subject to the approval of the Department, and under such reasonable rules and regulations as may be prescribed by the Department. An application for such approval shall be made jointly by the transferor and transferee. The transfer of a certificate of public convenience and necessity can only be made upon a satisfactory showing that such purchaser or transferee can and will comply with the applicable motor carrier or broker laws, rules and regulations of the Department, is fit, willing and able to properly perform the services, and all taxes due the Commonwealth have been paid, or payment guaranteed.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0022" target="_blank" rel="noreferrer">22</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0111" target="_blank" rel="noreferrer">111</a>.</p>',3),c=[i];function o(s,l,f,p,d,h){return a(),r("div",null,c)}const b=e(n,[["render",o]]);export{u as __pageData,b as default};
