import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-209.1 Release of vehicle information by Department to prospective vehicle purchasers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-209.1.md","filePath":"vacode/46.2-209.1.md"}'),o={name:"vacode/46.2-209.1.md"},a=t('<h1 id="§-46-2-209-1-release-of-vehicle-information-by-department-to-prospective-vehicle-purchasers" tabindex="-1">§ 46.2-209.1 Release of vehicle information by Department to prospective vehicle purchasers. <a class="header-anchor" href="#§-46-2-209-1-release-of-vehicle-information-by-department-to-prospective-vehicle-purchasers" aria-label="Permalink to &quot;§ 46.2-209.1 Release of vehicle information by Department to prospective vehicle purchasers.&quot;">​</a></h1><p>Notwithstanding the provisions of subsection A of § <a href="/vacode/46.2-208/">46.2-208</a>, the Commissioner may furnish vehicle information to a prospective purchaser of that vehicle, if the prospective purchaser completes an application therefor, including the vehicle&#39;s make, model, year, and vehicle identification number, and pays the fee prescribed by the Commissioner. Such information furnished by the Commissioner may be provided from the Department&#39;s own records, or may be obtained by the Commissioner through the National Motor Vehicle Title Information System or any other nationally recognized system providing similar information.</p><p>Notwithstanding the provisions of § <a href="/vacode/46.2-208/">46.2-208</a>, the Commissioner shall furnish vehicle information for a manufactured home to a bona fide prospective purchaser or home owner of such manufactured home, real estate agent, title insurer, settlement agent, attorney, manufactured home dealer, manufactured home broker, or loan officer, provided that any requester completes an application therefor, provides sufficient information to identify the manufactured home and the intent to purchase, and pays the fee prescribed by the Commissioner. Such information furnished by the Commissioner may be provided from the Department&#39;s own records or may be obtained by the Commissioner through the National Motor Vehicle Title Information System or any other nationally recognized system providing similar information.</p><p>Nothing in this section shall be construed to authorize the release of any personal information, driver information, or special identification card information as defined in § <a href="/vacode/46.2-208/">46.2-208</a>.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0092" target="_blank" rel="noreferrer">92</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0235" target="_blank" rel="noreferrer">235</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0257" target="_blank" rel="noreferrer">257</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0421" target="_blank" rel="noreferrer">421</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0479" target="_blank" rel="noreferrer">479</a>.</p>',5),n=[a];function s(h,c,l,f,p,m){return r(),i("div",null,n)}const u=e(o,[["render",s]]);export{v as __pageData,u as default};
