import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-2099.41 Certification requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2099.41.md","filePath":"vacode/46.2-2099.41.md"}'),a={name:"vacode/46.2-2099.41.md"},n=i('<h1 id="§-46-2-2099-41-certification-requirements" tabindex="-1">§ 46.2-2099.41 Certification requirements. <a class="header-anchor" href="#§-46-2-2099-41-certification-requirements" aria-label="Permalink to &quot;§ 46.2-2099.41 Certification requirements.&quot;">​</a></h1><p>A. A person may apply to the Department for certification as an operator of an excursion train. The Department shall certify an applicant if the Department determines that the applicant will operate a passenger train that:</p><ol><li><p>Is primarily used for tourism or public service; and</p></li><li><p>Leads to the promotion of the tourist industry in the Commonwealth.</p></li></ol><p>B. An application for certification shall include:</p><ol><li><p>The name and address of each person who owns an interest of at least 10 percent of the excursion train operation;</p></li><li><p>An address in the Commonwealth where the excursion train is based;</p></li><li><p>An operations plan, including the route to be used and a schedule of operations and stops along the route; and</p></li><li><p>Evidence of insurance that meets the requirements of subsection C.</p></li></ol><p>C. The Department shall not certify to a person under subsection A unless the person files with the Department evidence of insurance providing coverage of liability resulting from injury to persons or damages to property in the amount of at least $10 million for the operation of the train.</p><p>D. The Department shall not certify an applicant under subsection A if the applicant or any other person owning interest in the excursion train also owns or operates a regularly scheduled passenger train service with interstate connection.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0286" target="_blank" rel="noreferrer">286</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0431" target="_blank" rel="noreferrer">431</a>.</p>',8),o=[n];function s(l,p,c,h,f,d){return t(),r("div",null,o)}const _=e(a,[["render",s]]);export{m as __pageData,_ as default};
