import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-2129 Unlawful use of registration and identification markers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2129.md","filePath":"vacode/46.2-2129.md"}'),i={name:"vacode/46.2-2129.md"},n=r('<h1 id="§-46-2-2129-unlawful-use-of-registration-and-identification-markers" tabindex="-1">§ 46.2-2129 Unlawful use of registration and identification markers. <a class="header-anchor" href="#§-46-2-2129-unlawful-use-of-registration-and-identification-markers" aria-label="Permalink to &quot;§ 46.2-2129 Unlawful use of registration and identification markers.&quot;">​</a></h1><p>It shall be unlawful for any person to operate or cause to be operated on any highway in the Commonwealth any motor vehicle that (i) does not carry the proper registration and identification that this title requires, (ii) does not display an identification marker issued for such vehicle by the Department in such manner as is prescribed by the Department, or (iii) bears registration or identification markers of persons whose permit or certificate issued by the Department has been revoked, suspended, or renewal thereof denied in accordance with this chapter.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>.</p>',3),o=[n];function s(c,l,d,f,h,p){return t(),a("div",null,o)}const m=e(i,[["render",s]]);export{u as __pageData,m as default};
