import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-2134 Grounds for denying, suspending, or revoking permits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2134.md","filePath":"vacode/46.2-2134.md"}'),i={name:"vacode/46.2-2134.md"},n=o('<h1 id="§-46-2-2134-grounds-for-denying-suspending-or-revoking-permits" tabindex="-1">§ 46.2-2134 Grounds for denying, suspending, or revoking permits. <a class="header-anchor" href="#§-46-2-2134-grounds-for-denying-suspending-or-revoking-permits" aria-label="Permalink to &quot;§ 46.2-2134 Grounds for denying, suspending, or revoking permits.&quot;">​</a></h1><p>A permit issued under this chapter may be denied, suspended, or revoked on any one or more of the following grounds:</p><ol><li><p>Failure to submit to the Department any tax, fees, fines, or penalties owed to the Department.</p></li><li><p>Failure to maintain and keep on file with the Department motor carrier liability insurance or cargo insurance, issued by a company licensed to do business in the Commonwealth, or a bond, certificate of insurance, certificate of self-insurance, or unconditional letter of credit in accordance with this chapter, with respect to each motor vehicle operated in the Commonwealth.</p></li><li><p>Inactivity of a motor carrier as may be evidenced by the absence of a motor vehicle registered to operate under such permit for a period of greater than three months.</p></li></ol><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>.</p>',4),a=[n];function s(c,d,l,p,h,f){return r(),t("div",null,a)}const m=e(i,[["render",s]]);export{u as __pageData,m as default};
