import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-2137 Basis for reinstatement of suspended permits or certificates; reinstatement fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2137.md","filePath":"vacode/46.2-2137.md"}'),i={name:"vacode/46.2-2137.md"},n=a('<h1 id="§-46-2-2137-basis-for-reinstatement-of-suspended-permits-or-certificates-reinstatement-fees" tabindex="-1">§ 46.2-2137 Basis for reinstatement of suspended permits or certificates; reinstatement fees. <a class="header-anchor" href="#§-46-2-2137-basis-for-reinstatement-of-suspended-permits-or-certificates-reinstatement-fees" aria-label="Permalink to &quot;§ 46.2-2137 Basis for reinstatement of suspended permits or certificates; reinstatement fees.&quot;">​</a></h1><p>A. The Department shall reinstate any permit or certificate suspended pursuant to this chapter provided the grounds upon which the suspension action was taken have been satisfied and the appropriate reinstatement fee and other applicable fees have been paid to the Department.</p><p>B. The reinstatement fee for suspensions issued pursuant to this chapter shall be $50. In the event multiple credentials have been suspended under this chapter for the same violation only one reinstatement fee shall be applicable.</p><p>C. In addition to a reinstatement fee, a fee of $500 shall be paid for failure of a motor carrier to keep in force at all times insurance, a bond or bonds, in an amount required by this chapter. Any motor carrier who applies for a new permit or certificate because his prior permit or certificate was revoked for failure to keep in force at all times insurance, a bond or bonds, in an amount required by this chapter, shall also be subject to a fee of $500.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>.</p>',5),s=[n];function o(p,c,f,l,d,h){return t(),r("div",null,s)}const _=e(i,[["render",o]]);export{u as __pageData,_ as default};
