import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-2138 Basis for reissuance after revocation of permits or certificates; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2138.md","filePath":"vacode/46.2-2138.md"}'),i={name:"vacode/46.2-2138.md"},s=r('<h1 id="§-46-2-2138-basis-for-reissuance-after-revocation-of-permits-or-certificates-fees" tabindex="-1">§ 46.2-2138 Basis for reissuance after revocation of permits or certificates; fees. <a class="header-anchor" href="#§-46-2-2138-basis-for-reissuance-after-revocation-of-permits-or-certificates-fees" aria-label="Permalink to &quot;§ 46.2-2138 Basis for reissuance after revocation of permits or certificates; fees.&quot;">​</a></h1><p>The Department shall not accept an application for a permit or certificate from an applicant where such credentials have been revoked pursuant to this chapter until the period of revocation imposed by the Department has passed. The Department shall process such applications under the same provisions, procedures and requirements as an original application for such permit or certificate. The Department shall issue such permit or certificate, provided that the applicant has met all the appropriate qualifications and requirements, has satisfied the grounds upon which the revocation action was taken, and has paid the appropriate application or filing fees to the Department.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>.</p>',3),o=[s];function n(c,p,f,h,l,d){return a(),t("div",null,o)}const m=e(i,[["render",n]]);export{u as __pageData,m as default};
