import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-2139 Surrender of license plate and registration card; removal by law enforcement; operation of vehicle denied.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2139.md","filePath":"vacode/46.2-2139.md"}'),i={name:"vacode/46.2-2139.md"},o=a('<h1 id="§-46-2-2139-surrender-of-license-plate-and-registration-card-removal-by-law-enforcement-operation-of-vehicle-denied" tabindex="-1">§ 46.2-2139 Surrender of license plate and registration card; removal by law enforcement; operation of vehicle denied. <a class="header-anchor" href="#§-46-2-2139-surrender-of-license-plate-and-registration-card-removal-by-law-enforcement-operation-of-vehicle-denied" aria-label="Permalink to &quot;§ 46.2-2139 Surrender of license plate and registration card; removal by law enforcement; operation of vehicle denied.&quot;">​</a></h1><p>A. It shall be unlawful for a permittee or certificate holder whose permit or certificate has expired or been revoked or suspended or whose renewal thereof has been denied pursuant to this chapter to fail or refuse to surrender, on demand, to the Department license plates and registration cards issued under this title.</p><p>B. It shall be unlawful for a vehicle owner who is not the holder of a valid permit or certificate or whose vehicle is not validly leased to a motor carrier holding an active permit or certificate to fail or refuse to surrender to the Department on demand license plates and registration cards issued under this title.</p><p>C. If any law-enforcement officer finds that a vehicle bearing Virginia license plates or temporary transport plates is in violation of subsection A or B, such law-enforcement officer may remove the license plate or plates and registration card. If a law-enforcement officer removes a license plate or registration card, he shall forward such license plate and registration card to the Department.</p><p>D. When informed that a motor carrier vehicle is being operated in violation of this section, the driver shall drive the vehicle to a nearby location off the public highways and not remove it or allow it to be moved until the motor carrier is in compliance with all provisions of this chapter.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>.</p>',6),n=[o];function l(s,c,d,f,h,p){return r(),t("div",null,n)}const g=e(i,[["render",l]]);export{v as __pageData,g as default};
