import{_ as n,o,c as i,z as e,a as s}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-219 Bonds of Commissioner, Deputy Commissioners, assistants, administrators, and law-enforcement officers; liability insurance policies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-219.md","filePath":"vacode/46.2-219.md"}'),t={name:"vacode/46.2-219.md"},a=e("h1",{id:"§-46-2-219-bonds-of-commissioner-deputy-commissioners-assistants-administrators-and-law-enforcement-officers-liability-insurance-policies",tabindex:"-1"},[s("§ 46.2-219 Bonds of Commissioner, Deputy Commissioners, assistants, administrators, and law-enforcement officers; liability insurance policies. "),e("a",{class:"header-anchor",href:"#§-46-2-219-bonds-of-commissioner-deputy-commissioners-assistants-administrators-and-law-enforcement-officers-liability-insurance-policies","aria-label":'Permalink to "§ 46.2-219 Bonds of Commissioner, Deputy Commissioners, assistants, administrators, and law-enforcement officers; liability insurance policies."'},"​")],-1),r=e("p",null,"The Commissioner, the Deputy Commissioners, the assistant commissioners, the administrators, and law-enforcement officers appointed by the Commissioner and engaged in the enforcement of criminal laws and the laws relating to the operation of motor vehicles on the highways in the Commonwealth shall, before entering on or continuing in their duties, enter into bond with some solvent guaranty, indemnity, fidelity, or casualty company authorized to do business in the Commonwealth as surety, in the penalty of $100,000 and with condition for the faithful and lawful performance of their duties. These bonds shall be filed in the office of the Department and the premiums thereon shall be paid out of the fund appropriated for the enforcement of the laws concerning motor vehicles. All persons injured or damaged in any manner by the unlawful, negligent, or improper conduct of any such officer while on duty may maintain an action on the bond.",-1),c=e("p",null,"In lieu of posting bond as provided in this section, any assistant or law-enforcement officer may furnish an adequate liability insurance policy as proof of his ability to respond in damages which may be adjudged against him in favor of any person or persons injured or damaged in any manner resulting from his unlawful, negligent, or improper conduct while on official duty, to the amount of $100,000. The premiums on any such insurance policy or policies shall be paid out of the funds appropriated for the enforcement of the laws concerning motor vehicles.",-1),l=e("p",null,"All such bonds and insurance policies shall be approved by the Commissioner.",-1),d=e("p",null,"Code 1950, § 46-40; 1950, p. 221; 1958, c. 541, § 46.1-39; 1976, c. 78; 1989, c. 727.",-1),m=[a,r,c,l,d];function f(h,p,u,y,b,_){return o(),i("div",null,m)}const C=n(t,[["render",f]]);export{g as __pageData,C as default};
