import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 46.2-221.2 Extension of expiration of driver's licenses issued to certain persons in service to the United States government or for good cause shown.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-221.2.md","filePath":"vacode/46.2-221.2.md"}`),r={name:"vacode/46.2-221.2.md"},n=o('<h1 id="§-46-2-221-2-extension-of-expiration-of-driver-s-licenses-issued-to-certain-persons-in-service-to-the-united-states-government-or-for-good-cause-shown" tabindex="-1">§ 46.2-221.2 Extension of expiration of driver&#39;s licenses issued to certain persons in service to the United States government or for good cause shown. <a class="header-anchor" href="#§-46-2-221-2-extension-of-expiration-of-driver-s-licenses-issued-to-certain-persons-in-service-to-the-united-states-government-or-for-good-cause-shown" aria-label="Permalink to &quot;§ 46.2-221.2 Extension of expiration of driver&#39;s licenses issued to certain persons in service to the United States government or for good cause shown.&quot;">​</a></h1><p>A. Notwithstanding § <a href="/vacode/46.2-330/">46.2-330</a>, any driver&#39;s license that is issued by the Department under Chapter 3 (§ <a href="/vacode/46.2-300/">46.2-300</a> et seq.) to (i) a person serving outside the Commonwealth in the armed services of the United States, (ii) a person serving outside the Commonwealth as a member of the diplomatic service of the United States appointed under the Foreign Service Act of 1946, (iii) a civilian employee of the United States government or any agency or contractor thereof serving outside the United States on behalf of the United States government, or (iv) a spouse or dependent accompanying any such member of the armed services or diplomatic service serving outside the Commonwealth or civilian employee of the United States government or any agency or contractor thereof serving outside the United States on behalf of the United States government shall be held not to have expired during the period of the licensee&#39;s service outside the Commonwealth in the armed services of the United States or as a member of the diplomatic service of the United States appointed under the Foreign Service Act of 1946 or as a civilian employee of the United States government or any agency or contractor thereof serving outside the United States on behalf of the United States government and 180 days thereafter. However, no extension granted under this section shall exceed six years from the date of expiration shown on the individual&#39;s driver&#39;s license.</p><p>For the purposes of this subsection, &quot;service in the armed services of the United States&quot; includes active duty service with the regular Armed Forces of the United States or the National Guard or other reserve component.</p><p>B. Notwithstanding § <a href="/vacode/46.2-330/">46.2-330</a>, the Commissioner may, for good cause shown, extend the validity period of a driver&#39;s license issued by the Department pursuant to Chapter 3 (§ <a href="/vacode/46.2-300/">46.2-300</a> et seq.), provided that the license holder requesting the extension (i) contacts the Department prior to expiration of his license, (ii) is temporarily absent from the Commonwealth at the time his driver&#39;s license is due for renewal, (iii) provides the Commissioner with verifiable evidence documenting the need for an extension, (iv) provides the Commissioner with the earliest date of return, and (v) is not eligible to renew his license online. No extension granted under this subsection shall exceed two years from the date of expiration shown on the individual&#39;s driver&#39;s license.</p><p>C. The Department shall furnish to any person whose driver&#39;s license is extended under this section documentary or other proof that he is entitled to the benefits of this section when operating any motor vehicle.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0975" target="_blank" rel="noreferrer">975</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0085" target="_blank" rel="noreferrer">85</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0249" target="_blank" rel="noreferrer">249</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0589" target="_blank" rel="noreferrer">589</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0591" target="_blank" rel="noreferrer">591</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0547" target="_blank" rel="noreferrer">547</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0039" target="_blank" rel="noreferrer">39</a>.</p>',6),s=[n];function a(h,d,c,l,f,v){return t(),i("div",null,s)}const m=e(r,[["render",a]]);export{g as __pageData,m as default};
