import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-221.4 Grace period for replacement of license plates or decals and registrations for certain persons in service to the United States government.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-221.4.md","filePath":"vacode/46.2-221.4.md"}'),o={name:"vacode/46.2-221.4.md"},n=r('<h1 id="§-46-2-221-4-grace-period-for-replacement-of-license-plates-or-decals-and-registrations-for-certain-persons-in-service-to-the-united-states-government" tabindex="-1">§ 46.2-221.4 Grace period for replacement of license plates or decals and registrations for certain persons in service to the United States government. <a class="header-anchor" href="#§-46-2-221-4-grace-period-for-replacement-of-license-plates-or-decals-and-registrations-for-certain-persons-in-service-to-the-united-states-government" aria-label="Permalink to &quot;§ 46.2-221.4 Grace period for replacement of license plates or decals and registrations for certain persons in service to the United States government.&quot;">​</a></h1><p>Owners or lessees of vehicles registered in the Commonwealth who (i) have served outside of the United States in the armed services of the United States, (ii) have served outside the United States as a member of the diplomatic service of the United States appointed under the Foreign Service Act of 1946, (iii) have been a civilian employee of the United States government or any agency or contractor thereof serving outside the United States on behalf of the United States government, or (iv) are a spouse or dependent accompanying any such member of the armed services or diplomatic service serving outside the United States or civilian employee of the United States government or any agency or contractor thereof serving outside the United States on behalf of the United States government shall have a 90-day grace period, beginning on the date that such person is no longer serving outside the United States, in which to comply with the vehicle registration requirements of this title.</p><p>To be eligible for the grace period, the vehicle shall:</p><ol><li><p>Be owned or leased by a person or persons qualifying under this section;</p></li><li><p>Have had valid registration issued by the Department at the time the member of the armed services of the United States, member of the diplomatic service, civilian employee of the United States government, or any agency or contractor thereof began service outside of the United States;</p></li><li><p>Comply with the financial responsibility requirements of this title;</p></li><li><p>Display the latest license plates and decals issued by the Department for the vehicle; and</p></li><li><p>Be operated only by persons qualifying under this section while possessing:</p></li></ol><p>a. Orders or other military documentation demonstrating that they are entitled to the benefits of this section; and</p><p>b. The latest registration card issued by the Department for the vehicle.</p><p>Nothing in this section shall be construed to prohibit any person or persons who own or lease vehicles registered in the Commonwealth and are currently serving outside of the United States in the armed services of the United States from complying, when possible and as necessary, with the vehicle registration requirements of this title during the period of service outside the United States or while on leave in Virginia.</p><p>For the purposes of this section &quot;the armed services of the United States&quot; includes active duty service with the regular Armed Forces of the United States or the National Guard or other reserve component.</p><p>The provisions of this section shall not apply to special license plates issued to members of the National Guard under § <a href="http://law.lis.virginia.gov/vacode/46.2-744/" target="_blank" rel="noreferrer">46.2-744</a>.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0975" target="_blank" rel="noreferrer">975</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0591" target="_blank" rel="noreferrer">591</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0385" target="_blank" rel="noreferrer">385</a>.</p>',10),a=[n];function s(l,h,c,d,p,f){return t(),i("div",null,a)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
