import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-223 Additional powers and duties of Commissioner.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-223.md","filePath":"vacode/46.2-223.md"}'),i={name:"vacode/46.2-223.md"},n=o('<h1 id="§-46-2-223-additional-powers-and-duties-of-commissioner" tabindex="-1">§ 46.2-223 Additional powers and duties of Commissioner. <a class="header-anchor" href="#§-46-2-223-additional-powers-and-duties-of-commissioner" aria-label="Permalink to &quot;§ 46.2-223 Additional powers and duties of Commissioner.&quot;">​</a></h1><p>The Commissioner shall have the following powers and duties related to transportation safety:</p><ol><li><p>To evaluate safety measures currently in use by all transport operators in all modes which operate in or through the Commonwealth, with particular attention to the safety of equipment and appliances and methods and procedures of operation;</p></li><li><p>To engage in training and educational activities aimed at enhancing the safe transport of passengers and property in and through the Commonwealth;</p></li><li><p>To cooperate with all relevant entities of the federal government, including, but not limited to, the Department of Transportation, the Federal Railway Administration, the Federal Aviation Administration, the Coast Guard, and the Independent Transportation Safety Board in matters concerning transportation safety;</p></li><li><p>To initiate, conduct, and issue special studies on matters pertaining to transportation safety;</p></li><li><p>To evaluate transportation safety efforts, practices, and procedures of the agencies or other entities of the government of the Commonwealth and make recommendations to the Secretary of Transportation, the Governor, and the General Assembly on ways to increase transportation safety consciousness or improve safety practices;</p></li><li><p>To assist entities of state government and political subdivisions of the Commonwealth in enhancing their efforts to ensure safe transportation, including the dissemination of relevant materials and the rendering of technical or other advice;</p></li><li><p>To collect, tabulate, correlate, analyze, evaluate, and review the data gathered by various entities of the state government in regard to transportation operations, management, and accidents, especially the information gathered by the Department of Motor Vehicles, the Department of State Police, and the State Corporation Commission;</p></li><li><p>To develop, implement, and review, in conjunction with relevant state and federal entities, a comprehensive highway safety program for the Commonwealth, and to inform the public about it;</p></li><li><p>To assist towns, counties and other political subdivisions of the Commonwealth in the development, implementation, and review of local highway safety programs as part of the state program;</p></li><li><p>To review the activities, role, and contribution of various state entities to the Commonwealth&#39;s highway safety program and to report annually and in writing to the Governor and General Assembly on the status, progress, and prospects of highway safety in the Commonwealth;</p></li><li><p>To recommend to the Secretary of Transportation, the Governor, and the General Assembly any corrective measures, policies, procedures, plans, and programs which are needed to make the movement of passengers and property on the highways of the Commonwealth as safe as practicable;</p></li><li><p>To design, implement, administer, and review special programs or projects needed to promote highway safety in the Commonwealth;</p></li><li><p>To integrate highway safety activities into the framework of transportation safety in general;</p></li><li><p>To administer the Traffic Safety Fund established pursuant to § <a href="/vacode/46.2-749.2:10/">46.2-749.2:10</a> and to accept grants, gifts, bequests, and other moneys contributed to, deposited in, or designated for deposit in the Fund; and</p></li><li><p>Notwithstanding any other provision of this title, for the duration of a declared state of emergency as defined in § <a href="/vacode/44-146.16/">44-146.16</a> and for up to 90 days after the declaration of a state of emergency has been rescinded or expires, (i) to extend the validity or delay the cancellation of driver&#39;s licenses, special identification cards, and vehicle registrations; (ii) to extend the time frame during which a driver improvement clinic or payment plan may be completed; (iii) to extend the maximum number of days of residency permitted before a new resident must be licensed in Virginia pursuant to § <a href="/vacode/46.2-308/">46.2-308</a> to operate a motor vehicle in the Commonwealth; and (iv) to extend the time frame during which a new resident may operate a motor vehicle in the Commonwealth that has been duly registered in another jurisdiction before registering the vehicle in the Commonwealth.</p></li></ol><p>1984, c. 778, § 46.1-40.4; 1989, c. 727; 1990, cc. 1, 317; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0743" target="_blank" rel="noreferrer">743</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0129" target="_blank" rel="noreferrer">129</a>.</p>',4),r=[n];function s(d,l,h,p,c,m){return t(),a("div",null,r)}const g=e(i,[["render",s]]);export{u as __pageData,g as default};
