import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-2905 Knowledge test; waiting period prior to reexamination.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2905.md","filePath":"vacode/46.2-2905.md"}'),i={name:"vacode/46.2-2905.md"},o=a('<h1 id="§-46-2-2905-knowledge-test-waiting-period-prior-to-reexamination" tabindex="-1">§ 46.2-2905 Knowledge test; waiting period prior to reexamination. <a class="header-anchor" href="#§-46-2-2905-knowledge-test-waiting-period-prior-to-reexamination" aria-label="Permalink to &quot;§ 46.2-2905 Knowledge test; waiting period prior to reexamination.&quot;">​</a></h1><p>The Department shall examine every applicant for an escort vehicle driver certificate before issuing a Virginia escort vehicle driver certificate. Every applicant shall be required to take and pass an escort vehicle driver knowledge test. Prior to taking the knowledge test, the applicant shall present evidence that he has completed a state-approved escort vehicle driver certification training course pursuant to the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-2904/" target="_blank" rel="noreferrer">46.2-2904</a>.</p><p>Any person who applies for an escort vehicle driver certificate under § <a href="http://law.lis.virginia.gov/vacode/46.2-2906/" target="_blank" rel="noreferrer">46.2-2906</a> and fails the knowledge test administered pursuant to that section three times shall not be eligible for retesting for at least 30 days. A reexamination fee of $2 shall be charged for the second and subsequent test in the same manner as provided for driver license testing under the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-332/" target="_blank" rel="noreferrer">46.2-332</a>.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0312" target="_blank" rel="noreferrer">312</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>.</p>',4),n=[o];function s(l,c,d,p,h,g){return t(),r("div",null,n)}const _=e(i,[["render",s]]);export{v as __pageData,_ as default};
